Row.code:
try {
	var result = DataShapes["PTC.Action.ActionResult"].CreateValues();

	let reasonCategoriesRows = 0;
	if (reasonCategoriesTable != undefined) {
		reasonCategoriesRows = reasonCategoriesTable.getRowCount();
	}

	if (reasonCategoriesRows > 0 || equipment || status || owner) {
		let query = queryBuilder(equipment, reasonCategoriesTable, reasonCategoriesRows, status, owner);

		result = Resources["InfoTableFunctions"].Query({
			t: actionsData,
			query: query
		});
	} else {
		result = me.QueryActions({
			owner: owner,
			startDate: startDate,
			endDate: endDate,
			status: status,
			equipment: equipment,
			reasonCategoriesTable: reasonCategoriesTable,
			timeZoneId: timeZoneId
		});
	}
	if (searchTerm != "" && searchTerm != undefined && searchTerm != null) {
		result = me.SearchActions({
			actions: result,
			searchTerm: searchTerm,
			timeZoneId: timeZoneId
		});
	}

    result.AddField({name: "hoursSaved", baseType: "STRING"});
    result.rows.toArray().forEach(row => {
        let reasonCategoryUidTable = Resources["InfoTableFunctions"].CreateInfoTableFromDataShape({
            infoTableName: "InfoTable",
            dataShapeName: "PTC.ReasonCode.ReasonCategory"
        });
		reasonCategoryUidTable.AddRow({uid: row.reasonCategoryUid});

        let chartTrendTable = me.ProcessTrendDisplay({
            isSingleAction: true,
            reasonCategoryUid: reasonCategoryUidTable,
            endDate: row.endDate,
            includeEndDateEdge: true,
            timeZoneId: timeZoneId,
            modelUid: row.modelUid,
            reasonTreeNodeUid: row.reasonTreeNodeUid,
            startDate: row.startDate
        });

        let hoursSaved = me.QueryHoursSaved({
            chartTrendTable: chartTrendTable,
            reasonCategoryUid: row.reasonCategoryUid,
            baselineStartDate: row.baselineStartDate,
            timeZoneId: timeZoneId,
            baselineNumber: row.baselineHours,
            baselineEndDate: row.baselineEndDate,
            modelUid: row.modelUid,
            reasonTreeNodeUid: row.reasonTreeNodeUi
        });
        row.hoursSaved = hoursSaved;
    });


} catch (err) {
	logger.error("Thing [{}] Service [{}] error at line [{}] : {}", me.name, err.fileName, err.lineNumber, err);
	throw me.LocalizeToken({
		token: "PTC.Base.ErrorMessage"
	});
}

function queryBuilder(equipment, reasonCategoriesTable, reasonCategoriesRows, status, owner) {
	const query = {
		filters: {
			type: 'AND',
			filters: []
		}
	};
	const filters = query.filters.filters;

	if (equipment) {
		filters.push({
			fieldName: 'modelName',
			type: 'EQ',
			value: equipment
		});
	}

	if (reasonCategoriesRows > 0) {
		let reasonCategories = [];
		for (var i = 0; i < reasonCategoriesRows; i++) {
			reasonCategories.push(reasonCategoriesTable.rows[i].displayName);
		}
		filters.push({
			fieldName: "reasonCategoryName",
			type: 'IN',
			values: reasonCategories
		});
	}

	if (status) {
		filters.push({
			fieldName: 'statusDefinitionName',
			type: 'EQ',
			value: status
		});
	}

	if (owner) {
		filters.push({
			fieldName: 'owner',
			type: 'EQ',
			value: owner
		});
	}
	return query;
}

