function getBestDemonstratedSpeed() {
	let completedStatusUID = Things[adminConfigManager.GetJobOrderManager()].GetJobOrderDispatchStatuses().Find({
		statusName: "Completed"
	}).uid;

	let startDate = adminConfigManager.GetHistoricalDate();
	let endDate = new Date();

	let workDefinitionExecutionResponseFilter = {
		"filters": {
			"type": "AND",
			"filters": [{
					"type": "EQ",
					"fieldName": "enabled",
					"value": true
				}, {
					"type": "NOTMISSINGVALUE",
					"fieldName": "actualcycletime"
				}, {
					"type": "EQ",
					"fieldName": "status",
					"value": completedStatusUID
				}, {
					"type": "EQ",
					"fieldName": "modeluid",
					"value": modelUid
				},
				{
					"type": "EQ",
					"fieldName": "materialmasteruid",
					"value": materialMasterUid
				},
				{
					"type": "GE",
					"fieldName": "timestamp",
					"value": startDate
				},
				{
					"type": "LE",
					"fieldName": "timestamp",
					"value": endDate
				}
			]
		},
		"sorts": [{
			"dataShapeName": "PTCDTS.WorkDefinitionExecutionResponse",
			"fieldName": "actualcycletime",
			"isAscending": true
		}]
	};
	// result: INFOTABLE dataShape: "PTCDTS.WorkDefinitionExecutionResponse"
	let actualCycleTimes = workDefManager.GetWorkDefinitionExecutionResponses({
		filter: workDefinitionExecutionResponseFilter /* JSON */ ,
		limit: 1 /* INTEGER */
	});

	return actualCycleTimes.actualcycletime;
}

