function GetMaterialListOfWorkCenter() {
	let updatedFilter = {
        "sorts": [{
            "fieldName": "id",
            "isAscending": false,
            "dataShapeName": "PTC.MaterialMaster.MaterialMaster"
        }],
		"filters": {
			"filters": [],
            "type": "AND"
				}
    };

	if (updatedFilter && updatedFilter.filters && updatedFilter.filters.filters) {
		updatedFilter.filters.filters.push({
			"dataShapeName": "PTC.MfgModel.Model",
			//"alias": "Child_Model",
			"fieldName": "uid",
			"type": "EQ",
			"value": workUnitUid
		});
	}

	let joinFilter = {
		"joins": [{
				"type": "LEFT",
				"sourceDataShapeName": "PTC.MaterialMaster.MaterialMaster",
				"sourceFieldName": "uid",
				"targetDataShapeName": "PTC.WorkMaster.WorkMasterMaterialSpecification",
				"targetFieldName": "materialMasterUid"
			},
			{
				"type": "LEFT",
				"sourceDataShapeName": "PTC.WorkMaster.WorkMasterMaterialSpecification",
				"sourceFieldName": "workMasterUid",
				"targetDataShapeName": "PTC.WorkMaster.WorkMaster",
				"targetFieldName": "uid"
			},
			{
				"type": "LEFT",
				"sourceDataShapeName": "PTC.WorkMaster.WorkMaster",
				"sourceFieldName": "uid",
				"targetDataShapeName": "PTC.WorkMaster.WorkMasterConfigurationSpecification",
				"targetFieldName": "workMasterUid"
			},
			{
				"type": "LEFT",
				"sourceDataShapeName": "PTC.WorkMaster.WorkMaster",
				"sourceFieldName": "uid",
				"targetDataShapeName": "PTC.WorkMaster.WorkMasterModelSpecification",
				"targetFieldName": "workMasterUid"
			},
			{
				"type": "LEFT",
				"sourceDataShapeName": "PTC.WorkMaster.WorkMasterModelSpecification",
				"sourceFieldName": "modelUid",
				"targetDataShapeName": "PTC.MfgModel.Model",
				"targetFieldName": "uid"
			}
		]
	};
	let finalFilter = Things["PTC.DBConnection.QueryUtils"].CombineFilters({
		combineTypeOperator: "AND",
		filter1: updatedFilter,
		filter2: joinFilter
	});
	let materialMasterManager = me.GetConfiguredManagerForIdentifier({
        identifier: "PTC.MaterialMaster.Manager" /* STRING */
    });

	let output = Things[materialMasterManager].GetMaterialMasters({
		filter: finalFilter,
	});

	let result = DataShapes['PTC.AdminConfiguration.WorkMasterDetails'].CreateValues();
	output.rows.toArray().forEach(entity => {
		let newEntry = new Object();
		let bestDemonstratedSpeed = me.GetBestDemonstratedSpeed({
			modelUid: entity.PTC_WorkMaster_WorkMasterModelSpecification_modelUid,
			materialMasterUid: entity.uid
		});

        bestDemonstratedSpeed = bestDemonstratedSpeed || entity.PTC_WorkMaster_WorkMaster_duration;

		newEntry.materialId = entity.id;
		newEntry.materialUid = entity.uid;
		newEntry.idealCycleTime = entity.PTC_WorkMaster_WorkMaster_duration;
		newEntry.idealCycleTimeWithUnits = formatIdealCycleTimeWithUnits(entity.PTC_WorkMaster_WorkMaster_duration);
		newEntry.productionBlockValue = getProductionBlockDurationForMaterial(entity.PTC_WorkMaster_WorkMasterConfigurationSpecification_productionBlockValue);
		newEntry.productionBlockValueWithUnits = getProductionBlockDurationWithUnitsForMaterial(entity.PTC_WorkMaster_WorkMasterConfigurationSpecification_productionBlockValue);
		newEntry.bestDemonstratedSpeed = formatCycleTime(bestDemonstratedSpeed);
		newEntry.bestDemonstratedSpeedWithUnits = formatCycleTimeWithUnits(bestDemonstratedSpeed);
		newEntry.workmasterConfigurationSpecificationUid = entity.Child_Model_uid;
		newEntry.workmasterUid = entity.PTC_WorkMaster_WorkMasterModelSpecification_workMasterUid;
		newEntry.warningIcon = (entity.PTC_WorkMaster_WorkMaster_duration > bestDemonstratedSpeed) ? "/Thingworx/MediaEntities/PTC.Administration.WarningIcon_MD" : "";
		newEntry.useWorkCenterSettings = (entity.PTC_WorkMaster_WorkMasterConfigurationSpecification_productionBlockValue === undefined) ? true : false;

		result.AddRow(newEntry);
	});

	return result;
}

function getProductionBlockDurationForMaterial(productionDurationValue) {
	let productionBlockDuration;
	if (productionDurationValue === undefined) {
		let productionBlockDurationInUnits = adminConfigManager.GetDefaultMaterialSettingsOnEquipment({
			equipmentUid: getWorkCenterUidFromWorkUnit(workUnitUid) /* STRING */
		});
		productionBlockDuration = adminConfigManager.ConvertTimeUnitValuesIntoDurationValue({
			timeInHours: productionBlockDurationInUnits.productionBlockSettingsHours /* INTEGER */ ,
			timeInMinutes: productionBlockDurationInUnits.productionBlockSettingsMinutes /* INTEGER */ ,
			timeInSeconds: undefined /* INTEGER */
		});
	} else {
		productionBlockDuration = productionDurationValue;
	}

	return productionBlockDuration;

}

function getProductionBlockDurationWithUnitsForMaterial(productionDurationValue) {
	let productionBlockDuration;
	let useWorkCenterSettings = false;
	if (productionDurationValue === undefined) {
		let productionBlockDurationInUnits = adminConfigManager.GetDefaultMaterialSettingsOnEquipment({
			equipmentUid: getWorkCenterUidFromWorkUnit(workUnitUid) /* STRING */
		});
		productionBlockDuration = adminConfigManager.ConvertTimeUnitValuesIntoDurationValue({
			timeInHours: productionBlockDurationInUnits.productionBlockSettingsHours /* INTEGER */ ,
			timeInMinutes: productionBlockDurationInUnits.productionBlockSettingsMinutes /* INTEGER */ ,
			timeInSeconds: undefined /* INTEGER */
		});
		useWorkCenterSettings = true;
	} else {
		productionBlockDuration = productionDurationValue;
	}

	return formatProductionDurationValue(productionBlockDuration, useWorkCenterSettings);

}

function formatProductionDurationValue(productionDurationValue, useWorkCenterSettings) {
	let productionDurationInTimeUnits = adminConfigManager.ConvertDurationValueIntoTimeUnits({
		productionBlockDurationValue: productionDurationValue /* NUMBER */
	});
	let hourValue = productionDurationInTimeUnits.hours;
	let minuteValue = productionDurationInTimeUnits.minutes;

	let formattedValue = "";
	if (hourValue != 0) {
		formattedValue = hourValue + " " + localize("PTC.Administration.Hours") + " ";
	}
	if (minuteValue != 0) {
		formattedValue = formattedValue + minuteValue + " " + localize("PTC.Administration.Minutes");
	}
	if (useWorkCenterSettings) {
		return formattedValue;
	} else {
		return formattedValue + "*";
	}

}

function formatCycleTime(cycleTime) {
	if (cycleTime != undefined) {
		return cycleTime.toFixed(1);
	} else {
		return null;
	}
}

function formatCycleTimeWithUnits(cycleTime) {
	if (cycleTime != undefined) {
		return cycleTime.toFixed(1) + " " + localize("PTC.Administration.Seconds");
	} else {
		return null;
	}
}
function formatIdealCycleTimeWithUnits(cycleTime) {
    if (cycleTime != undefined) {
        return cycleTime.toFixed(4) + " " + localize("PTC.Administration.Seconds");
    } else {
        return null;
    }
}

function localize(token) {
	let message = adminConfigManager.GenerateLocalizationTokensDynamically({
		localizationToken: token /* STRING */ ,
		value: " " /* STRING */
	});
	return message;
}

function getWorkCenterUidFromWorkUnit(workUnitUid){
    let workCenter = mfgModelManager.GetWorkcenterFromWorkunit({
        workunitUid: workUnitUid /* STRING [Required] */
    });
     return workCenter.uid;
}

