    var result = DataShapes["PTC.MaterialMaster.MaterialMaster"].CreateValues();
    const MODEL_THING = Things[thingName];
    validateModel(MODEL_THING);

	let isWorkUnit = Things[me.GetMfgModelManager()].IsModelType({
        uid: MODEL_THING.GetPTCModelUid(),
        type: "Workunit"
    });
    let pacemakerModel = {};
    if(isWorkUnit){
        pacemakerModel.thingName = thingName;
    } else {
        pacemakerModel = MODEL_THING.GetPTCPacemakerWorkUnitInWorkCenter({
            workcenterUid: MODEL_THING.GetPTCModelUid()
        });
    }


    if (pacemakerModel && (isWorkUnit || pacemakerModel.length > 0)) {
        const pacemakerThingName = Things[pacemakerModel.thingName];
        validateModel(pacemakerThingName);

        result = pacemakerThingName.GetPTCAnalysisWorkMasterMaterials();
    }
} catch (err) {
    logger.error("Thing [{}] Service [{}] error at line [{}] : {}", me.name, err.fileName, err.lineNumber, err);
    throw localize("PTC.Base.ErrorMessage");
}

function validateModel(model) {
    if (!model) {
        throw new Error("Model information is missing.");
    }
}

function localize(token) {
    return me.LocalizeToken({
        stringToken: token
    });
}

