    datasetRef: datasetRef,
    queryFields: queryFieldsTable,
    enableMissingValueImputation: false
};

let productionBlockUids = [];
try {
    let distributionResultTable = dataThing.DistributionQuerySynchronous(distributionQueryParams);
    distributionResultTable.rows.toArray().forEach(row => {
        row.groupByValues.rows.toArray().forEach(value => {
            productionBlockUids.push(Math.abs(value.fieldValue));
        });
    });
}
catch (err) {
    logger.error(serviceName+": Error running distribution query '" + distributionQueryParams + "': "+err);
}
let result = unique(productionBlockUids).join(",");

