let stats = dataThing.DistributionStatisticsQuerySynchronous({
    fields    : queryField,
    locale    : undefined,
    datasetRef: datasetRef,
    enableMissingValueImputation: true,
});

let hours = 0.0;
if (stats && stats.getRowCount() > 0) {
    hours = stats.rows[0].sumOfAllObservations;
    hours = hours/(isLossCategory ? 3600 : 60);
}
result = hours;

// **************************************************
// ****  P R I V A T E  F U N C T I O N S  **********
// **************************************************

me.UpdatePerformanceAnalysisMasterMenuConfig();
// Return true as a default
var result = true;

// table: INFOTABLE dataShape: ""
let table = Things["PTC.PerformanceAnalysis.Manager"].GetConfigurationTable({
	tableName: "MenuConfiguration" /* STRING */
});

table.rows.toArray().forEach(row => {
    if(row.menuName == "MasterMenuNavigation"){
        let submenu = row.submenu;
        submenu.rows.toArray().forEach(menu => {
            if(menu.menuTitleKey == "PTC.PerformanceAnalysis.Waterfall"){
                menu.menuItemMashupName = "PTCDTS.PerformanceAnalysis.Analysis_MU";
            }
        });

    }
});

Things["PTC.PerformanceAnalysis.Manager"].SetMultiRowConfigurationTable({
	configurationTable: table /* INFOTABLE */,
	persistent: true /* BOOLEAN {"defaultValue":true} */,
	tableName: "MenuConfiguration" /* STRING */
});

let result = table;
