		thingName: thingName,
		eventUid: eventUid,
		eventTypeUid: eventCategory.reasonTypeUid,
		eventCategoryUid: eventCategory.reasonCategoryUid,
		reasonTypeUid: reasonCodeElements.reasonTypeUid,
		reasonCategoryUid: reasonCodeElements.reasonCategoryUid,
		reasonTreeNodeUid: reasonTreeNodeUid,
		modelUid: modelUid,
		reasonModelUid: reasonModelUid,
		jobOrderUid: productionBlock.jobOrderUid,
		materialMasterUid: productionBlock.materialMasterUid,
		productionBlockUid: productionBlock.productionBlockUid,
		notes: comments,
		quantity: impact,
		lossValue: impact * CONVERT_MIN_TO_SEC,
		reallocateFromGoodCount: reallocateFromGoodCount,
		sourceEventUid: sourceEventUid,
		dataShapeName: dataShapeName,
		eventTime: eventTime
	});
} catch (err) {
	logger.error("Thing [{}] Service [{}] error at line [{}] : {}", me.name, err.fileName, err.lineNumber, err);
	throw me.Localize({
		token: "PTC.Base.ErrorMessage"
	});
}

function validateInformation(thingName, impact, isAutomation) {
	if (!thingName) {
		throw new Error("Pacemaker information is missing.");
	}

	if (!isAutomation && impact == 0) {
		throw new Error("Impact value can not be 0.");
	}
}

