	let mashupName = getNoEventLogMashupName();
	let mashupMessage = me.Localize({token: "PTC.ProductionDashboard.NoCurrentPBEvents"});
	let countOfLogItems = logItems ? logItems.rows.length : 0;

	if (countOfLogItems > 0) {
		logItems = filterResultsBySearchText(searchText, logItems);
		if (logItems.length > 0) {
			logItems = sortResults(logItems);

			if (logItems.length <= 0) {
				mashupMessage = (sortingOption == 'Alerts Only') && isCollapsed ? me.Localize({token: "PTC.ProductionDashboard.NoAlertsMessage"}) : (sortingOption == 'Scrap Only' || sortingOption == 'Alerts Only') && !isCollapsed ? me.Localize({token: "PTC.ProductionDashboard.NoEventsSearchAndFilterMessage"}) : mashupMessage;
