				mashupName = isCollapsed ? "PTCDTS.ProductionDashboard.CollapsedEventLogGrid_MU" : "PTCDTS.ProductionDashboard.ExpandedEventLogGrid_MU";
			}
		} else {
			mashupMessage = me.Localize({token: "PTC.ProductionDashboard.NoEventsSearchAndFilterMessage"});
		}
	}
	result.AddRow({
		"count": countOfLogItems,
		"items": logItems,
		"mashupName": mashupName,
		"mashupMessage": mashupMessage
	});
} catch (err) {
	logger.error("{} - {} : {} - {}", me.name, err.fileName, err.lineNumber, err);
	throw me.Localize({token: "PTC.Base.ErrorMessage"});
}

function getNoEventLogMashupName() {
	let configuredMashupName = '';
	try {
		configuredMashupName = me.GetConfiguredMashupForIdentifier({
			identifier: "NoEventLogView"
		});
	} catch (err) {
		configuredMashupName = 'PTC.ProductionDashboard.NoEventLogs_MU';
	}
	return configuredMashupName;
}

function filterResultsBySearchText(searchText, logItems) {
	let searchResult = logItems;
	if (searchText && searchText !== '') {
		searchResult = Resources["InfoTableFunctions"].LikeFilter({
			inclusive: true /* BOOLEAN {"defaultValue":true} */ ,
			fieldName: JOB_ORDER_ID /* STRING */ ,
			t: logItems /* INFOTABLE */ ,
			pattern: '%' + searchText + '%' /* STRING */
		});
	}
	return searchResult;
}

function filterResultsByScrap(logItems) {
	let filteredByScrapResult = logItems;
	filteredByScrapResult = Resources["InfoTableFunctions"].EQFilter({
		fieldName: 'eventTypeName' ,
		isCaseSensitive: false ,
		t: logItems ,
		value: 'Scrap'
	});
	return filteredByScrapResult;
}

function filterByAlerts(logItems) {
	let filteredByAlertsResult = logItems;
	filteredByAlertsResult = Resources["InfoTableFunctions"].EQFilter({
		fieldName: 'hasChildrenReasons' /* STRING */ ,
		isCaseSensitive: false /* BOOLEAN {"defaultValue":false} */ ,
		t: logItems /* INFOTABLE */ ,
		value: true /* STRING */
	});
	return filteredByAlertsResult;
}

function sortByAlerts(logItems) {
	let sortByAlertsResult = logItems;
	sortByAlertsResult.Sort({
		name: 'hasChildrenReasons',
		ascending: false
	});
	return sortByAlertsResult;
}

function sortResults(logItems) {
	switch (sortingOption) {
		case "Scrap Only":
			return filterResultsByScrap(logItems);
		case "Alerts Only":
			return filterByAlerts(logItems);
		case "Alerts First":
			return sortByAlerts(logItems);
		default:
			return logItems;
	}
}

const model = Things[thingName];
result = DataShapes["PTC.ProductionDashboard.EventLogs"].CreateValues();
sortingOption = sortingOption || 'Most Recent';
useHTML = useHTML || false;

if (model) {
	try {
		const MODEL_TYPE = model.GetPTCModelType();
		const baseCommonHelper = Things[me.GetBaseCommonHelper()];
		const TIME_ZONE_ID = model.GetPTCSiteFromModel().timeZone;
		const INTERNATIONAL_DATE_PATTERN = me.Localize({
			token: "PTC.Base.InternationalDateTimeFormat"
		});
		const INTERNATIONAL_TIME_PATTERN = me.Localize({
			token: "PTC.Base.InternationalShortTimeFormat"
		});
		const USER_LOCALE = baseCommonHelper.GetCurrentUserLocale();
		const LOCALIZED_PARTS = me.Localize({
			token: "PTC.ProductionDashboard.Parts"
		});
		const LOCALIZED_PART = me.Localize({
			token: "PTC.ProductionDashboard.PartLabel"
		});
		const LOCALIZED_MIN = me.Localize({
			token: "PTC.ProductionDashboard.Minute"
		});
		const AVAILABILITY_EVENT = "PTC.OperationKPI.AvailabilityEvent";
		const LOSS_EVENT = "PTC.OperationKPI.LossEvent";
		const OVERTIME = me.Localize({
			token: "PTC.ProductionDashboard.Overtime"
		});
		const PRODUCTION_EVENT = "PTC.OperationKPI.ProductionEvent";
		const MISSING = me.Localize({
			token: "PTC.ProductionDashboard.Missing"
		});
		const finalIndexes = [];
		const REASON_CATEGORY_DS = "PTC.ReasonCode.ReasonCategory";
		const REASON_CATEGORY_FIELD_NAME = "name";
		const REASON_DS = "PTC.ReasonCode.Reason";
		const REASON_FIELD_NAME = "name";

        //for scrap we need to show 1 week data
        if (sortingOption === 'Scrap Only') {
            startDate = (Date.now() - 168 * 60 * 60 * 1000);
        }

        if (MODEL_TYPE.name == "Workunit" || MODEL_TYPE.name == "Workcenter") {
            result = me.GetTimeRangeLossEventLogs({
                thingName: thingName,
                startDateTime: startDate || (Date.now() - 24 * 60 * 60 * 1000),
                endDateTime: endDate || Date.now()
            });
        } else {
            logger.error("Thing [{}] Service [{}] error at line [{}] : {}", me.name);
            throw me.Localize({
                token: "PTC.Base.ErrorMessage"
            });
        }

		if (result && result.rows) {
			sortByEventTimeInDescendingOrder();
			result.rows.toArray().forEach((row, index) => {
				if (!row.reasonTreeNodeUid || row.reasonTreeNodeUid == 0) {
					row.reasonTreeNodeUid = "RC_UID_" + row.reasonCategoryUid;
					row.hasChildrenReasons = true;
				}
				row.shiftName = row.shiftName ? row.shiftName : OVERTIME;
				row.reasonName = row.reasonName ? row.reasonName : "";
				populateHTMLFields(row);
				row.impact = getImpact(row);

				if (row.productionBlockStartTime) {
					row.productionBlockLocalizedStartTimeDisplay = baseCommonHelper.FormatDateTime({
						dateTime: row.productionBlockStartTime,
						timeZoneId: TIME_ZONE_ID,
						pattern: INTERNATIONAL_DATE_PATTERN,
						localeId: USER_LOCALE
					});
				}
				if (row.hasChildrenReasons) {
					finalIndexes.push(index);
				}
			});
		}
	} catch (err) {
		logger.error("{} - {}:{} - {}", me.name, err.fileName, err.lineNumber, err);
		throw me.Localize({
			token: "PTC.Base.ErrorMessage"
		});
	}
}

function sortByEventTimeInDescendingOrder() {
    result.Sort({
        name: 'eventTime',
        ascending: false
    });
}

function populateHTMLFields(row) {
	row.impactDisplay = useHTML ? getImpactDisplay(row) : row.impact;
	row.eventTimeDisplay = useHTML ? getEventTimeDisplay(row) : "";
	row.categoryIcon = (useHTML && row.eventCategoryName && MediaEntities["PTC.ProductionDashboard." + row.eventCategoryName.split(" ").join("") + '_MD']) ? "<span><img id='img' width='10' src='/Thingworx/MediaEntities/PTC.ProductionDashboard." + row.eventCategoryName.split(" ").join("") + '_MD' + "'/></span>" : "";
	row.commentIcon = useHTML ? getCommentColumnIcons(row) : "";
}

function getImpact(row) {
    let impact;
    switch (row.dataShapeName) {
        case LOSS_EVENT:
            impact = Math.round(row.impact / 60);
            break;
        default:
            impact = row.impact;
    }
    return impact;
}

function getImpactDisplay(row) {
    let impactDisplay;
    let showAsAutomation = row.isAutomation;
    switch (row.dataShapeName) {
        case AVAILABILITY_EVENT:
            impactDisplay = row.endDate ? String(+parseFloat(row.impact).toFixed(1)) + " " + LOCALIZED_MIN : "";
            //showAsAutomation = true;
            break;
        case LOSS_EVENT:
            impactDisplay = Math.round(row.impact / 60) + " " + LOCALIZED_MIN;
            break;
        case PRODUCTION_EVENT:
            impactDisplay = row.impact + " " + ((row.impact === 1) ? LOCALIZED_PART : LOCALIZED_PARTS);
            break;
        default:
            impactDisplay = "";
    }
    return "<span style='font-size:14px;" + (showAsAutomation ? "color:#9EA2A8;" : "") + "'>" + impactDisplay + ((showAsAutomation && impactDisplay) ? " *" : "") + "</span>";
}

function getEventTimeDisplay(row) {
    let eventTimeDisplay = "";
    if (row.isAutomation && row.dataShapeName === AVAILABILITY_EVENT) {

        let startDate = baseCommonHelper.FormatDateTime({
            dateTime: row.startDate,
            timeZoneId: TIME_ZONE_ID,
            pattern: INTERNATIONAL_TIME_PATTERN,
            localeId: USER_LOCALE
        });
        eventTimeDisplay = "<span style='font-size:14px;color:#9EA2A8'>" + startDate;
        if (row.endDate) {
            // result: STRING
            let endDate = baseCommonHelper.FormatDateTime({
                dateTime: row.endDate,
                timeZoneId: TIME_ZONE_ID,
                pattern: INTERNATIONAL_TIME_PATTERN,
                localeId: USER_LOCALE
            });
            eventTimeDisplay += " - " + endDate;
        }
        eventTimeDisplay += "</span>";
    }
    return eventTimeDisplay;
}

function getCommentColumnIcons(row) {
    let commentIconsDisplay = "";
    if (row.comments) {
        commentIconsDisplay = "<span style='margin-left:5px'><img id='comment' width='18' height='18' src='/Thingworx/MediaEntities/PTC.ProductionDashboard.CommentIcon_MD'/></span>";
    }

    if ((row.hasChildrenReasons || !row.reasonName) && commentIconsDisplay) {
        commentIconsDisplay = commentIconsDisplay + "<span style='margin-left:8px'><img id='comment' width='18' height='18' src='/Thingworx/MediaEntities/PTC.ProductionDashboard.ErrorIcon_MD'/></span>";
    } else if (row.hasChildrenReasons || !row.reasonName) {
        commentIconsDisplay = "<span style='margin-left:5px'><img id='comment' width='18' height='18' src='/Thingworx/MediaEntities/PTC.ProductionDashboard.ErrorIcon_MD'/></span>";
    }

    return commentIconsDisplay;
}

