try {
    let isManualAvailabilityEventsEnabled = me.IsManualAvailabilityEventsEnabled();
    // Assume Start and End Time are in Site time, convert to server time
    if(localTimeZoneId){
        let model = Things[thingName];
        const MODEL_TYPE = model.GetPTCModelType();
        const baseCommonHelper = Things[me.GetBaseCommonHelper()];
        const TIME_ZONE_ID = model.GetPTCSiteFromModel().timeZone;
        const SITE_TIME_ZONE_OFFSET_MIN = me.GetTimeZoneOffset({timeZoneId: TIME_ZONE_ID});
        const LOCAL_TIME_ZONE_OFFSET_MIN = me.GetTimeZoneOffset({timeZoneId: localTimeZoneId});
        let timeZoneOffsetGap = SITE_TIME_ZONE_OFFSET_MIN - LOCAL_TIME_ZONE_OFFSET_MIN;
        let locale = baseCommonHelper.GetCurrentUserLocale();

		if(StartTime){
        	StartTime = convertLocaleTimeToUtc(StartTime,timeZoneOffsetGap);
        }
        if(EndTime){
        	EndTime = convertLocaleTimeToUtc(EndTime,timeZoneOffsetGap);
        }
    }


	let operationKPIManager = Things[me.GetConfiguredManagerForIdentifier({
		identifier: "PTC.OperationKPI.Manager"
	})];

	const AVAILABILITY_EVENT = "PTC.OperationKPI.AvailabilityEvent";
	let eventParams = {
		comments: comments /* STRING */ ,
		eventCategory: eventCategory /* INFOTABLE {"dataShape":"PTC.ReasonCode.ReasonTreeGrid"} */ ,
		eventUid: undefined /* STRING */ ,
		impact: impact /* NUMBER [Required] */ ,
		reallocateFromGoodCount: reallocateFromGoodCount /* BOOLEAN {"defaultValue":false} */ ,
		thingName: thingName /* THINGNAME [Required] */ ,
		productionBlock: productionBlock /* INFOTABLE [Required] {"dataShape":"PTC.ProductionDashboard.ProductionBlock"} */ ,
		dataShapeName: dataShapeName /* DATASHAPENAME */ ,
		isAutomation: false /* BOOLEAN {"defaultValue":false} */ ,
		reasonCodeElements: reasonCodeElements /* INFOTABLE {"dataShape":"PTC.ReasonCode.ReasonTreeGrid"} */ ,
		eventTime: undefined /* DATETIME */ ,
		modelUid: modelUid /* STRING */ ,
		reasonModelUid: reasonModelUid /* STRING */ ,
		sourceEventUid: sourceEventUid /* STRING */
	};

    let reasonTypeId = reasonCodeElements.reasonTypeUid;
	// If reason Code is not scrap or speed Loss
	if (isManualAvailabilityEventsEnabled && reasonTypeId != 6 && reasonTypeId != 4) {
		eventParams.impact = 1;
		eventParams.dataShapeName = AVAILABILITY_EVENT;

		StartTime.setSeconds(0, 0);
		eventParams.eventTime = StartTime;

		if (eventUid != undefined && eventUid != null) {
			//if we are updating an existing event, we will delete the original event and create a new one
			operationKPIManager.DeleteAvailabilityEvent({
				uid: eventUid /* STRING [Required] */
			});
		}

		me.OverrideEventsInTimeRange({
			StartTime: StartTime /* DATETIME */ ,
			EndTime: EndTime /* DATETIME */ ,
			thingName: thingName /* THINGNAME */
		});

	} else {
		eventParams.eventUid = eventUid;
	}

	me.CreateOrUpdateEvent(eventParams);


} catch (err) {
	logger.error("Thing [{}] Service [{}] error at line [{}] : {}", me.name, err.fileName, err.lineNumber, err);
	throw (err);
}

function convertLocaleTimeToUtc(date, localOffset) {
	return dateAddMinutes(date, -1*localOffset);
}

function validateInformation(thingName, impact, isAutomation) {
	if (!thingName) {
		throw new Error("Pacemaker information is missing.");
	}

	if (!isAutomation && impact == 0) {
		throw new Error("Impact value can not be 0.");
	}
}

