result.AddRow(newEntry);

// CreateInfoTableFromDataShape(infoTableName:STRING("InfoTable"), dataShapeName:STRING):INFOTABLE(PTC.ProductionDashboard.StatusTimelineData)
let result = Resources["InfoTableFunctions"].CreateInfoTableFromDataShape({
    infoTableName: "InfoTable",
    dataShapeName: "PTCDTS.ProductionDashboard.StatusTimelineData"
});

const model = Things[thingName];

try{
    const MODEL_TYPE = model.GetPTCModelType();
    const baseCommonHelper = Things[me.GetBaseCommonHelper()];
    const TIME_ZONE_ID = model.GetPTCSiteFromModel().timeZone;
    const SITE_TIME_ZONE_OFFSET_MIN = me.GetTimeZoneOffset({timeZoneId: TIME_ZONE_ID});
    const LOCAL_TIME_ZONE_OFFSET_MIN = me.GetTimeZoneOffset({timeZoneId: localTimeZoneId});
    let timeZoneOffsetGap = SITE_TIME_ZONE_OFFSET_MIN - LOCAL_TIME_ZONE_OFFSET_MIN;
    let locale = baseCommonHelper.GetCurrentUserLocale();
    // get reason categories and create color json
    let reasonCodeManager = Things[me.GetConfiguredManagerForIdentifier({
            identifier: "PTC.ReasonCode.Manager"
        })];
    let reasonCategories = reasonCodeManager.GetReasonCategories();

    let color = {};
    reasonCategories.rows.toArray().forEach(row => {
        color[row.uid] = "#" + row.color;
    });

    const PADDING = 3;
    let productionAccoutingStartEndTime = me.GetProductionAccountingStartEndTime({
        isShiftView: isShiftView,
        thingName: thingName
    });
    let startDateTime = productionAccoutingStartEndTime.startDateTime;
    let endDateTime = productionAccoutingStartEndTime.isShiftAvailable ? productionAccoutingStartEndTime.endDateTime : dateAddHours(productionAccoutingStartEndTime.endDateTime, PADDING);

    // result: INFOTABLE dataShape: "PTC.OperationKPI.AllEvents"
    let events = model.GetPTCAllEvents({
        startDateTime: startDateTime /* DATETIME [Required] */,
        endDateTime: endDateTime /* DATETIME [Required] */,
        locale:locale
    });

    events = removeScrapAndSpeedLossEvents(events);


    // CreateInfoTableFromDataShape(infoTableName:STRING("InfoTable"), dataShapeName:STRING):INFOTABLE(PTCDTS.ProductionDashboard.EventDataShape)
    let timelineData = Resources["InfoTableFunctions"].CreateInfoTableFromDataShape({
        infoTableName: "InfoTable",
        dataShapeName: "PTCDTS.ProductionDashboard.EventDataShape"
    });

    events.rows.toArray().forEach(row => {
        // PTCDTS.ProductionDashboard.EventDataShape entry object
        let newEntry = {
            start: convertUtcToLocaleTime(row.eventTime,timeZoneOffsetGap), // DATETIME
            end: row.endTime ? convertUtcToLocaleTime(row.endTime,timeZoneOffsetGap) : convertUtcToLocaleTime(new Date(),timeZoneOffsetGap), // DATETIME
            color: color[row.reasonCategoryUid],// STRING
            reason: row.reasonCategoryName
        };

        timelineData.AddRow(newEntry);
    });


    // PTC.ProductionDashboard.StatusTimelineData entry object
    let newEntry = {
        startTime: convertUtcToLocaleTime(startDateTime, timeZoneOffsetGap), // DATETIME
        endTime: convertUtcToLocaleTime(endDateTime, timeZoneOffsetGap), // DATETIME
        resource: thingName, // STRING
        events: timelineData // INFOTABLE 
    };

    result.AddRow(newEntry);
} catch (err) {
    logger.error("{} - {}:{} - {}", me.name, err.fileName, err.lineNumber, err);
    throw me.Localize({token: "PTC.Base.ErrorMessage"});
}

function removeScrapAndSpeedLossEvents(events){
    let params = {
        fieldName: "eventTypeName" /* STRING */,
        t: events /* INFOTABLE */,
        value: "Production" /* STRING */
    };

    // result: INFOTABLE
    events = Resources["InfoTableFunctions"].NEFilter(params);

    params = {
        fieldName: "eventTypeName" /* STRING */,
        t: events /* INFOTABLE */,
        value: "Scrap" /* STRING */
    };

    // result: INFOTABLE
    events = Resources["InfoTableFunctions"].NEFilter(params);

    params = {
        fieldName: "eventTypeName" /* STRING */,
        t: events /* INFOTABLE */,
        value: "SpeedLoss" /* STRING */
    };

    // result: INFOTABLE
    events = Resources["InfoTableFunctions"].NEFilter(params);

    return events;
}

function convertUtcToLocaleTime(date, localOffset) {
	return dateAddMinutes(date, localOffset);
}

// table: INFOTABLE dataShape: ""
let table = me.GetConfigurationTable({
	tableName: "CustomizationConfiguration" /* STRING */
});

let result;

table.rows.toArray().forEach(row => {
    if(row.Feature == "ManualAvailabilityEvents"){
        result = row.Enabled;
    }
});

// table: INFOTABLE dataShape: ""
let table = me.GetConfigurationTable({
	tableName: "CustomizationConfiguration" /* STRING */
});

let result;

table.rows.toArray().forEach(row => {
    if(row.Feature == "StatusTimeline"){
        result = row.Enabled;
    }
});

let operationKPIManager = Things[me.GetConfiguredManagerForIdentifier({
	identifier: "PTC.OperationKPI.Manager"
})];

let currentEventFlag = false;
if (EndTime == null || EndTime == undefined) {
	currentEventFlag = true;
	EndTime = new Date();
} else {
	EndTime.setSeconds(0, 0);
}

//get existing availability events in time period
let filter = {
	"filters": {
		"filters": [{
				"dataShapeName": "PTC.OperationKPI.AvailabilityEvent",
				"fieldName": "eventTime",
				"type": "GE",
				"value": StartTime
			},
			{
				"dataShapeName": "PTC.OperationKPI.AvailabilityEvent",
				"fieldName": "eventTime",
				"type": "LE",
				"value": EndTime
			},
			{
				"dataShapeName": "PTC.OperationKPI.AvailabilityEvent",
				"fieldName": "modelUid",
				"type": "EQ",
				"value": Things[thingName].GetPTCModelUid()
			}
		],
		"type": "AND"
	},
	"sorts": [{
		"fieldName": "eventTime",
		"isAscending": true,
		"isCaseSensitive": false
	}]
};

// result: INFOTABLE dataShape: "PTC.OperationKPI.AvailabilityEvent"
let existingEvents = operationKPIManager.GetAvailabilityEvents({
	filter: filter /* JSON */ ,
	offset: undefined /* INTEGER */ ,
	limit: undefined /* INTEGER */
});

if (existingEvents && existingEvents.rows && existingEvents.rows.length > 0) {

	let lastEventUid = existingEvents.rows[existingEvents.rows.length - 1].uid;
	existingEvents.rows.toArray().forEach(event => {
		if (event.uid != lastEventUid || currentEventFlag) {
			//There are either later events in this time period or this time period is being written as the current event. Delete the events
			operationKPIManager.DeleteAvailabilityEvent({
				uid: event.uid /* STRING [Required] */
			});

		} else {
			event.eventTime = EndTime;

			// CreateInfoTableFromDataShape(infoTableName:STRING("InfoTable"), dataShapeName:STRING):INFOTABLE(PTC.OperationKPI.AvailabilityEvent)
			let updatedEvents = Resources["InfoTableFunctions"].CreateInfoTableFromDataShape({
				infoTableName: "InfoTable",
				dataShapeName: "PTC.OperationKPI.AvailabilityEvent"
			});

			updatedEvents.AddRow(event);

			operationKPIManager.UpdateAvailabilityEvents({
				availabilityEvents: updatedEvents /* INFOTABLE {"dataShape":"PTC.OperationKPI.AvailabilityEvent"} */
			});
		}
	});
} else if (!currentEventFlag) {
	//No events found in time period and the added event is not a current event, find latest event and add a record to the table with eventtime = endtime
	let latestEvent = me.GetLatestEvent({
		thingName: thingName /* THINGNAME */ ,
		timestamp: StartTime
	});

	if (latestEvent && latestEvent.rows && latestEvent.rows.length > 0) {
		latestEvent.rows[0].eventTime = EndTime;
		// result: INFOTABLE dataShape: "PTC.OperationKPI.AvailabilityEvent"
		operationKPIManager.AddAvailabilityEvents({
			availabilityEvents: latestEvent /* INFOTABLE {"dataShape":"PTC.OperationKPI.AvailabilityEvent"} */
		});
	}
}

if (DataChanges.Values && DataChanges.Values.rows) {
    let workDefinitionExecutionResponseData = DataChanges.Values;
    //Create PB infotable
    // CreateInfoTableFromDataShape(infoTableName:STRING("InfoTable"), dataShapeName:STRING):INFOTABLE(PTC.ProductionDashboard.ProductionBlock)
    let productionBlock = Resources["InfoTableFunctions"].CreateInfoTableFromDataShape({
        infoTableName: "InfoTable",
        dataShapeName: "PTC.ProductionDashboard.ProductionBlock"
    });

    // These 3 fields are the only ones used in later services. ProductionBlockUid is only used for Loss or Production events so its okay that we dont have it
    let newEntry = {
        productionBlockUid: undefined, // STRING
        jobOrderUid: workDefinitionExecutionResponseData.joborderuid, // STRING
        materialMasterUid: workDefinitionExecutionResponseData.materialmasteruid, // STRING
    };

    productionBlock.AddRow(newEntry);

    //Get the thingname from the model uid

    let mfgModelManager = Things[me.GetConfiguredManagerForIdentifier({
        identifier: "PTC.MfgModel.Manager"
    })];

    // result: INFOTABLE dataShape: "PTC.MfgModel.Model"
    let thingName = mfgModelManager.GetWorkunit({
        uid: workDefinitionExecutionResponseData.modeluid /* STRING [Required] */
    }).thingName;

    let isAutomated = Things[thingName].IsPTCAutomationWorkUnit();

    if(!isAutomated){
        let StartStopJobOrderReasonMapping = me.GetConfigurationTable({
            tableName: "StartStopJobOrderReasonMapping" /* STRING */
        });

        if(StartStopJobOrderReasonMapping && StartStopJobOrderReasonMapping.rows && StartStopJobOrderReasonMapping.rows.length > 0)
        {
            let reasonCodeElements;
            if (StartStopJobOrderReasonMapping.StartJobOrderReasonUid && workDefinitionExecutionResponseData.status == "9") {
				reasonCodeElements = me.GetReasonTreeGridForReasonUid({
                    ReasonUid: StartStopJobOrderReasonMapping.StartJobOrderReasonUid /* STRING */
                });
            }
            else if(StartStopJobOrderReasonMapping.StopJobOrderReasonUid && (workDefinitionExecutionResponseData.status == "6" || workDefinitionExecutionResponseData.status == "3" || workDefinitionExecutionResponseData.status == "4")) {
                reasonCodeElements = me.GetReasonTreeGridForReasonUid({
                    ReasonUid: StartStopJobOrderReasonMapping.StopJobOrderReasonUid /* STRING */
                });
            }

            if(reasonCodeElements){
                me.AddManualAvailabilityEvents({
                    StartTime: workDefinitionExecutionResponseData.timestamp /* DATETIME */,
                    productionBlock: productionBlock /* INFOTABLE {"dataShape":"PTC.ProductionDashboard.ProductionBlock"} */,
                    EndTime: undefined /* DATETIME */,
                    impact: 1 /* NUMBER */,
                    thingName: thingName /* THINGNAME */,
                    reasonCodeElements: reasonCodeElements /* INFOTABLE {"dataShape":"PTC.ReasonCode.ReasonTreeGrid"} */,
                    eventCategory: reasonCodeElements /* INFOTABLE {"dataShape":"PTC.ReasonCode.ReasonTreeGrid"} */,
                    modelUid: workDefinitionExecutionResponseData.modeluid /* STRING */,

                });
            }
        }
    }
}
