function validateInformation(thingName, impact, isAutomation) {
	if (!thingName) {
		throw new Error("Pacemaker information is missing.");
	}

	if (!isAutomation && impact == 0) {
		throw new Error("Impact value can not be 0.");
	}
}

let model = Things[thingName];
try {
	let shiftInstances = model.GetPTCShiftInstancesByDateRange({
		startDateTime: date,
		endDateTime: dateAddHours(date, 24)
	});
	let shiftInstancesLength = shiftInstances.getRowCount();
	let startTime = date;
	filteredShiftInstances = DataShapes["PTC.ProductionDashboard.ShiftInstances"].CreateValues();
	shiftInstances.rows.toArray().forEach(row => {
		if (row.actualStartTime >= date) {
			filteredShiftInstances.AddRow({
				startTime: row.actualStartTime,
				endTime: row.actualEndTime,
				shiftName: row.shiftName
			});
		} else {
			startTime = row.actualEndTime;
		}
	});
	if (filteredShiftInstances.getRowCount() > 0) {
		//check for first and last gap
		startOfFirstShift = shiftInstances.getRow(0).actualStartTime;
		endOfLastShift = filteredShiftInstances.getRow(filteredShiftInstances.getRowCount() - 1).endTime;
		dateDiffStart = dateDifference(startOfFirstShift, startTime);
		dateDiffEnd = dateDifference(dateAddHours(date, 24), endOfLastShift);
		if (dateDiffStart > 0) {
			getChunksOfGap(startTime, startOfFirstShift);
		}
		if (dateDiffEnd > 0) {
			getChunksOfGap(endOfLastShift, dateAddHours(date, 24));
		}
		//Chunk for gaps in between shifts
		for (i = 0; i < shiftInstancesLength - 1; i++) {
			SDT = shiftInstances.getRow(i).actualEndTime;
			EDT = shiftInstances.getRow(i + 1).actualStartTime;
			dateDiff = dateDifference(EDT, SDT);
			if (dateDiff > 0) {
				getChunksOfGap(SDT, EDT);
			}
		}
		let sortedMergedTables = Resources["InfoTableFunctions"].Sort({
			sortColumn: "startTime",
			t: filteredShiftInstances,
			ascending: true
		});
		result = sortedMergedTables;
	} else {
		filteredShiftInstances = DataShapes["PTC.ProductionDashboard.ShiftInstances"].CreateValues();
		getChunksOfGap(startTime, dateAddHours(date, 24));
		result = filteredShiftInstances;
	}
} catch (err) {
	logger.error("Thing [{}] Service [{}] error at line [{}] : {}", me.name, err.fileName, err.lineNumber, err);
	throw me.Localize({
		token: "PTC.Base.ErrorMessage"
	});
}
function hasProduction(startTime, endTime) {
	let productionBlocks = model.GetPTCProductionBlockInfos({
		startDateTime: startTime,
		endDateTime: endTime
	});
	return productionBlocks.getRowCount() > 0;
}
function getChunksOfGap(startTime, endTime) {
	let UnplannedSplitHours = me.GetConfigurationTableRow({
		tableName: "DefaultConfiguration",//"DefaultConfigurationForUnplannedSplitHours",
		key: "UnplannedSplitHours"
	});
	UnplannedSplitHours = parseInt(UnplannedSplitHours.value, 10) || 8;
	while (startTime < endTime) {
		computedEndTime = dateMin(endTime, dateAddHours(startTime, UnplannedSplitHours));
		if (hasProduction(startTime, computedEndTime)) {
			filteredShiftInstances.AddRow({
				startTime: startTime,
				endTime: computedEndTime,
				shiftName: me.Localize({
					token: "PTC.ProductionDashboard.NoShift"
				})
			});
		}
		startTime = computedEndTime;
	}
	return filteredShiftInstances;
}

var result = DataShapes["PTC.ProductionDashboard.EventLogData"].CreateValues();
const JOB_ORDER_ID = 'jobOrderId';
sortingOption = sortingOption || 'All Events';
try {
    let logItems = me.GetEventLogs({
		startDate: startDate /* DATETIME */ ,
		endDate: endDate /* DATETIME */ ,
		sortingOption: sortingOption /* STRING */ ,
		thingName: thingName /* THINGNAME */ ,
		searchText: searchText /* STRING */ ,
		useHTML: useHTML /* BOOLEAN */ ,
		localTimeZoneId: localTimeZoneId
    });

	let isCollapsed = isCollapsedView || false;
	let mashupName = getNoEventLogMashupName();
	let mashupMessage = me.Localize({token: "PTC.ProductionDashboard.NoCurrentPBEvents"});
	let countOfLogItems = logItems ? logItems.rows.length : 0;

	if (countOfLogItems > 0) {
		logItems = filterResultsBySearchText(searchText, logItems);
		if (logItems.length > 0) {
			logItems = sortResults(logItems);

			if (logItems.length <= 0) {
				mashupMessage = (sortingOption == 'Alerts Only') && isCollapsed ? me.Localize({token: "PTC.ProductionDashboard.NoAlertsMessage"}) : (sortingOption == 'Scrap Only' || sortingOption == 'Alerts Only') && !isCollapsed ? me.Localize({token: "PTC.ProductionDashboard.NoEventsSearchAndFilterMessage"}) : mashupMessage;
			} else {
				mashupName = isCollapsed ? "PTCDTS.ProductionDashboard.CollapsedEventLogGrid_MU" : "PTCDTS.ProductionDashboard.ExpandedEventLogGrid_MU";
			}
		} else {
			mashupMessage = me.Localize({token: "PTC.ProductionDashboard.NoEventsSearchAndFilterMessage"});
		}
	}
	result.AddRow({
		"count": countOfLogItems,
		"items": logItems,
		"mashupName": mashupName,
		"mashupMessage": mashupMessage
	});
} catch (err) {
	logger.error("{} - {} : {} - {}", me.name, err.fileName, err.lineNumber, err);
	throw me.Localize({token: "PTC.Base.ErrorMessage"});
}

function getNoEventLogMashupName() {
	let configuredMashupName = '';
	try {
		configuredMashupName = me.GetConfiguredMashupForIdentifier({
			identifier: "NoEventLogView"
		});
	} catch (err) {
		configuredMashupName = 'PTC.ProductionDashboard.NoEventLogs_MU';
	}
	return configuredMashupName;
}

function filterResultsBySearchText(searchText, logItems) {
	let searchResult = logItems;
	if (searchText && searchText !== '') {
		searchResult = Resources["InfoTableFunctions"].LikeFilter({
			inclusive: true /* BOOLEAN {"defaultValue":true} */ ,
			fieldName: JOB_ORDER_ID /* STRING */ ,
			t: logItems /* INFOTABLE */ ,
			pattern: '%' + searchText + '%' /* STRING */
		});
	}
	return searchResult;
}

function filterResultsByScrap(logItems) {
	let filteredByScrapResult = logItems;
	filteredByScrapResult = Resources["InfoTableFunctions"].EQFilter({
		fieldName: 'eventTypeName' ,
		isCaseSensitive: false ,
		t: logItems ,
		value: 'Scrap'
	});
	return filteredByScrapResult;
}

function filterByAlerts(logItems) {
	let filteredByAlertsResult = logItems;
	filteredByAlertsResult = Resources["InfoTableFunctions"].EQFilter({
		fieldName: 'hasChildrenReasons' /* STRING */ ,
		isCaseSensitive: false /* BOOLEAN {"defaultValue":false} */ ,
		t: logItems /* INFOTABLE */ ,
		value: true /* STRING */
	});
	return filteredByAlertsResult;
}

function sortByAlerts(logItems) {
	let sortByAlertsResult = logItems;
	sortByAlertsResult.Sort({
		name: 'hasChildrenReasons',
		ascending: false
	});
	return sortByAlertsResult;
}

function sortResults(logItems) {
	switch (sortingOption) {
		case "Scrap Only":
			return filterResultsByScrap(logItems);
		case "Alerts Only":
			return filterByAlerts(logItems);
		case "Alerts First":
			return sortByAlerts(logItems);
		default:
			return logItems;
	}
}

const ALL_EVENTS = 'All Events';
const ALERTS_ONLY = 'Alerts Only';
const SCRAP_ONLY = 'Scrap Only';
const ALERTS_FIRST = 'Alerts First';
const HISTORICAL_EVENTS = 'Historical Events';

var result = DataShapes["PTC.ProductionDashboard.EventLogData"].CreateValues();

sortingOption = sortingOption || ALL_EVENTS;

try {
	let logItems = me.GetEventLogsInfo({
		startDate: startDate,
		endDate: endDate,
		sortingOption: sortingOption,
		thingName: thingName,
		useHTML: useHTML,
		localTimeZoneId: localTimeZoneId,
		pageNumber: pageNumber,
		pageSize: pageSize,
		filterAutomatedOrManual: filterAutomatedOrManual ? filterAutomatedOrManual : undefined,
		jobOrderUids: filterJobOrder ? getCommaSeparatedUids(filterJobOrder) : undefined,
		materialMasterUids: filterMaterial ? getCommaSeparatedUids(filterMaterial) : undefined,
		productionBlockUids: filterProductionBlock ? filterProductionBlock : undefined,
		eventCategoryUids: filterEventCategory ? getCommaSeparatedUids(filterEventCategory) : undefined,
		reasonCategoryUids: filterLossReason ? getReasonCategoryUids(filterLossReason) : undefined,
		reasonTreeNodeUids: filterLossReason ? getReasonTreeNodeUids(filterLossReason) : undefined,
		filterPBStartDateTime: filterPBStartDateTime ? filterPBStartDateTime : undefined,
		filterPBEndDateTime: filterPBEndDateTime ? filterPBEndDateTime : undefined,
		searchComment: searchComment ? searchComment : undefined,
		sortQuery: sortQuery
	});

	if (sortingOption == ALERTS_FIRST) {
		logItems = sortByAlerts(logItems);
	}

	let isCollapsed = isCollapsedView || false;
	let mashupName = getNoEventLogMashupName();
	let mashupMessage = me.Localize({
		token: "PTC.ProductionDashboard.NoWorkCenterMessage"
	});
	if (thingName) {
		mashupMessage = me.Localize({
			token: "PTC.ProductionDashboard.NoCurrentPBEvents" /* STRING [Required] */
		});
	}
	let countOfLogItems = logItems ? logItems.rows.length : 0;

	let isFiltersApplied = false;
	let isFiltersAppliedForAllEvents = false;

	isFiltersApplied = logItems.length > 0 && ((sortingOption == HISTORICAL_EVENTS) || (parseInt(filterAutomatedOrManual) >= 0) || (filterJobOrder ? filterJobOrder.rows.length > 0 : false) || (filterMaterial ? filterMaterial.rows.length > 0 : false) || (filterEventCategory ? filterEventCategory.rows.length > 0 : false) || (filterLossReason ? filterLossReason.rows.length > 0 : false) || searchComment || (filterPBStartDateTime && filterPBStartDateTime));
	isFiltersAppliedForAllEvents = sortingOption == ALL_EVENTS && (parseInt(filterAutomatedOrManual) >= 0) || (filterJobOrder ? filterJobOrder.rows.length > 0 : false) || (filterMaterial ? filterMaterial.rows.length > 0 : false) || (filterEventCategory ? filterEventCategory.rows.length > 0 : false) || (filterLossReason ? filterLossReason.rows.length > 0 : false) || searchComment || (filterPBStartDateTime && filterPBStartDateTime);

	if (countOfLogItems > 0) {
		mashupName = isCollapsed ? "PTCDTS.ProductionDashboard.CollapsedEventLogGrid_MU" : "PTCDTS.ProductionDashboard.ExpandedEventLogGrid_MU";
	} else {
		mashupMessage = (sortingOption == ALERTS_ONLY && isCollapsed) ? me.Localize({
			token: "PTC.ProductionDashboard.NoAlertsMessage"
		}) : (sortingOption == ALERTS_ONLY || sortingOption == HISTORICAL_EVENTS || isFiltersAppliedForAllEvents) && !isCollapsed ? me.Localize({
			token: "PTC.ProductionDashboard.NoEventsFilterMessage"
		}) : mashupMessage;
		if (thingName && isFiltersApplied && !isCollapsed) {
			mashupName = "PTC.ProductionDashboard.ExpandedEventLogGrid_MU";
		}
	}

	result.AddRow({
		"count": countOfLogItems,
		"items": logItems,
		"mashupName": mashupName,
		"mashupMessage": mashupMessage
	});
} catch (err) {
	logger.error("{} - {} : {} - {}", me.name, err.fileName, err.lineNumber, err);
	throw me.Localize({
		token: "PTC.Base.ErrorMessage"
	});
}

function getNoEventLogMashupName() {
	let configuredMashupName = '';
	try {
		configuredMashupName = me.GetConfiguredMashupForIdentifier({
			identifier: "NoEventLogView"
		});
	} catch (err) {
		configuredMashupName = 'PTC.ProductionDashboard.NoEventLogs_MU';
	}
	return configuredMashupName;
}

function sortByAlerts(logItems) {
	let sortByAlertsResult = logItems;
	sortByAlertsResult.Sort({
		name: 'hasChildrenReasons',
		ascending: false
	});
	return sortByAlertsResult;
}

function getCommaSeparatedUids(filterInfotable) {
	let uidArr = [];
	filterInfotable.rows.toArray().forEach(row => {
		uidArr.push(row.uid);
	});
	return uidArr.toString();
}

function getReasonCategoryUids(filterLossReason) {
	let reasonCategoryUids = [];
	filterLossReason.rows.toArray().forEach(row => {
		if (row.parentReasonTreeNodeUid == '' || row.parentReasonTreeNodeUid == undefined) {
			reasonCategoryUids.push(row.reasonCategoryUid);
		}
	});
	return reasonCategoryUids.toString();
}

function getReasonTreeNodeUids(filterLossReason) {
	let reasonTreeNodeUids = [];
	filterLossReason.rows.toArray().forEach(row => {
		if (row.parentReasonTreeNodeUid != '' && row.parentReasonTreeNodeUid != undefined) {
			reasonTreeNodeUids.push(row.reasonTreeNodeUid);
		}
	});
	return reasonTreeNodeUids.toString();
}

const model = Things[thingName];
result = DataShapes["PTC.ProductionDashboard.EventLogs"].CreateValues();
sortingOption = sortingOption || 'Most Recent';
useHTML = useHTML || false;

if (model) {
	try {
		const MODEL_TYPE = model.GetPTCModelType();
		const baseCommonHelper = Things[me.GetBaseCommonHelper()];
		const TIME_ZONE_ID = model.GetPTCSiteFromModel().timeZone;
		const INTERNATIONAL_DATE_PATTERN = me.Localize({
			token: "PTC.Base.InternationalDateTimeFormat"
		});
		const INTERNATIONAL_TIME_PATTERN = me.Localize({
			token: "PTC.Base.InternationalShortTimeFormat"
		});
		const USER_LOCALE = baseCommonHelper.GetCurrentUserLocale();
		const LOCALIZED_PARTS = me.Localize({
			token: "PTC.ProductionDashboard.Parts"
		});
		const LOCALIZED_PART = me.Localize({
			token: "PTC.ProductionDashboard.PartLabel"
		});
		const LOCALIZED_MIN = me.Localize({
			token: "PTC.ProductionDashboard.Minute"
		});
		const AVAILABILITY_EVENT = "PTC.OperationKPI.AvailabilityEvent";
		const LOSS_EVENT = "PTC.OperationKPI.LossEvent";
		const OVERTIME = me.Localize({
			token: "PTC.ProductionDashboard.Overtime"
		});
		const PRODUCTION_EVENT = "PTC.OperationKPI.ProductionEvent";
		const MISSING = me.Localize({
			token: "PTC.ProductionDashboard.Missing"
		});
		const finalIndexes = [];
		const REASON_CATEGORY_DS = "PTC.ReasonCode.ReasonCategory";
		const REASON_CATEGORY_FIELD_NAME = "name";
		const REASON_DS = "PTC.ReasonCode.Reason";
		const REASON_FIELD_NAME = "name";

		//for scrap we need to show 1 week data
		if (sortingOption === 'Scrap Only') {
			startDate = (Date.now() - 168 * 60 * 60 * 1000);
		}

		if (MODEL_TYPE.name == "Workunit" || MODEL_TYPE.name == "Workcenter") {
			result = me.GetTimeRangeLossEventLogs({
				thingName: thingName,
				startDateTime: startDate || (Date.now() - 24 * 60 * 60 * 1000),
				endDateTime: endDate || Date.now()
			});
		} else {
			logger.error("Thing [{}] Service [{}] error at line [{}] : {}", me.name);
			throw me.Localize({
				token: "PTC.Base.ErrorMessage"
			});
		}

		if (result && result.rows) {
			sortByEventTimeInDescendingOrder();
			result.rows.toArray().forEach((row, index) => {
				if (!row.reasonTreeNodeUid || row.reasonTreeNodeUid == 0) {
					row.reasonTreeNodeUid = "RC_UID_" + row.reasonCategoryUid;
					row.hasChildrenReasons = true;
				}
				row.shiftName = row.shiftName ? row.shiftName : OVERTIME;
				row.reasonName = row.reasonName ? row.reasonName : "";
				populateHTMLFields(row);
				row.impact = getImpact(row);

				if (row.productionBlockStartTime) {
					row.productionBlockLocalizedStartTimeDisplay = baseCommonHelper.FormatDateTime({
						dateTime: row.productionBlockStartTime,
						timeZoneId: TIME_ZONE_ID,
						pattern: INTERNATIONAL_DATE_PATTERN,
						localeId: USER_LOCALE
					});
				}
				if (row.hasChildrenReasons) {
					finalIndexes.push(index);
				}
			});
		}
	} catch (err) {
		logger.error("{} - {}:{} - {}", me.name, err.fileName, err.lineNumber, err);
		throw me.Localize({
			token: "PTC.Base.ErrorMessage"
		});
	}
}

function sortByEventTimeInDescendingOrder() {
	result.Sort({
		name: 'eventTime',
		ascending: false
	});
}

function populateHTMLFields(row) {
	row.impactDisplay = useHTML ? getImpactDisplay(row) : row.impact;
	row.eventTimeDisplay = useHTML ? getEventTimeDisplay(row) : "";
	row.categoryIcon = (useHTML && row.eventCategoryName && MediaEntities["PTC.ProductionDashboard." + row.eventCategoryName.split(" ").join("") + '_MD']) ? "<span><img id='img' width='10' src='/Thingworx/MediaEntities/PTC.ProductionDashboard." + row.eventCategoryName.split(" ").join("") + '_MD' + "'/></span>" : "";
	row.commentIcon = useHTML ? getCommentColumnIcons(row) : "";
}

function getImpact(row) {
	let impact;
	switch (row.dataShapeName) {
		case LOSS_EVENT:
			impact = Math.round(row.impact / 60);
			break;
		default:
			impact = row.impact;
	}
	return impact;
}

function getImpactDisplay(row) {
	let impactDisplay;
	let showAsAutomation = row.isAutomation;
	switch (row.dataShapeName) {
		case AVAILABILITY_EVENT:
			impactDisplay = row.endDate ? String(+parseFloat(row.impact).toFixed(1)) + " " + LOCALIZED_MIN : "";
			//showAsAutomation = true;
			break;
		case LOSS_EVENT:
			impactDisplay = Math.round(row.impact / 60) + " " + LOCALIZED_MIN;
			break;
		case PRODUCTION_EVENT:
			impactDisplay = row.impact + " " + ((row.impact === 1) ? LOCALIZED_PART : LOCALIZED_PARTS);
			break;
		default:
			impactDisplay = "";
	}
	return "<span style='font-size:14px;" + (showAsAutomation ? "color:#9EA2A8;" : "") + "'>" + impactDisplay + ((showAsAutomation && impactDisplay) ? " *" : "") + "</span>";
}

function getEventTimeDisplay(row) {
	let eventTimeDisplay = "";
	if (row.isAutomation && row.dataShapeName === AVAILABILITY_EVENT) {

		let startDate = baseCommonHelper.FormatDateTime({
			dateTime: row.startDate,
			timeZoneId: TIME_ZONE_ID,
			pattern: INTERNATIONAL_TIME_PATTERN,
			localeId: USER_LOCALE
		});
		eventTimeDisplay = "<span style='font-size:14px;color:#9EA2A8'>" + startDate;
		if (row.endDate) {
			// result: STRING
			let endDate = baseCommonHelper.FormatDateTime({
				dateTime: row.endDate,
				timeZoneId: TIME_ZONE_ID,
				pattern: INTERNATIONAL_TIME_PATTERN,
				localeId: USER_LOCALE
			});
			eventTimeDisplay += " - " + endDate;
		}
		eventTimeDisplay += "</span>";
	}
	return eventTimeDisplay;
}

function getCommentColumnIcons(row) {
	let commentIconsDisplay = "";
	if (row.comments) {
		commentIconsDisplay = "<span style='margin-left:5px'><img id='comment' width='18' height='18' src='/Thingworx/MediaEntities/PTC.ProductionDashboard.CommentIcon_MD'/></span>";
	}

	if ((row.hasChildrenReasons || !row.reasonName) && commentIconsDisplay) {
		commentIconsDisplay = commentIconsDisplay + "<span style='margin-left:8px'><img id='comment' width='18' height='18' src='/Thingworx/MediaEntities/PTC.ProductionDashboard.ErrorIcon_MD'/></span>";
	} else if (row.hasChildrenReasons || !row.reasonName) {
		commentIconsDisplay = "<span style='margin-left:5px'><img id='comment' width='18' height='18' src='/Thingworx/MediaEntities/PTC.ProductionDashboard.ErrorIcon_MD'/></span>";
	}

	return commentIconsDisplay;
}

let result = DataShapes["PTC.ProductionDashboard.EventLogs"].CreateValues();
try {
	const AVAILABILITY_EVENT = "PTC.OperationKPI.AvailabilityEvent";
	const LOSS_EVENT = "PTC.OperationKPI.LossEvent";
	const PRODUCTION_EVENT = "PTC.OperationKPI.ProductionEvent";
	const SEC_IN_MIN = 60;
	const PARTS = me.Localize({
		token: "PTC.ProductionDashboard.Materials"
	});
	const PART = me.Localize({
		token: "PTC.ProductionDashboard.Part"
	});
	let model = Things[thingName];
	const USER_LOCALE = me.GetCurrentUserLocale();
	const baseCommonHelper = Things[me.GetBaseCommonHelper()];

	var reasonCodeManager = me.GetConfiguredManagerForIdentifier({
		identifier: "PTC.ReasonCode.Manager"
	});
	if (model && productionBlockUid) {
		let operationKpiManager = model.GetPTCOperationExecutionManager();
		let negativeReasonTypes = Things[operationKpiManager].GetReasonTypeFilter({
			includeLoss: true,
			includeProduction: true,
			includeAvailability: true,
			includeNegative: true
		}).split(",");

		let reasonTypeUids = model.GetPTCReasonTypes({
			filter: {
				filters: {
					fieldName: "name",
					type: "IN",
					values: negativeReasonTypes
				}
			}
		}).rows.toArray().map(row => row.uid);

		let productionBlockAllEvents = model.GetPTCProductionBlockEvents({
			productionBlockUid: productionBlockUid,
			searchWorkUnitCausingEvent: searchWorkUnitCausingEvent,
			searchReasonCategory: searchReasonCategory,
			searchLossReason: searchLossReason,
			locale: USER_LOCALE
		});

		let lossEvents = productionBlockAllEvents.rows.toArray().filter(row => reasonTypeUids.includes(parseInt(row.reasonTypeUid)) == true);

		lossEvents.forEach(event => {
			setHasChildrenReasons(event);
			let impact = getImpact(event);
			let impactDisplay = getImpactDisplay(impact, event);
			result.AddRow({
				eventId: event.eventUid + event.dataShapeName, // STRING
				eventTime: event.eventTime, // DATETIME
				dataShapeName: event.dataShapeName, // DATASHAPENAME
				hasChildrenReasons: event.hasChildrenReasons,
				impact: impact, // NUMBER
				impactDisplay: impactDisplay, // HTML
				impactDuration: getImpactDuration(event),
				reasonName: event.reasonName || event.reasonCategoryName, // STRING
				startDate: event.eventTime,
				endDate: (event.isAutomation || event.dataShapeName == AVAILABILITY_EVENT) ? getEventEndTime(event) : event.endTime,
				eventTypeName: event.eventTypeName,
				reasonCategoryName: event.reasonCategoryName, // STRING
				reasonModelName:event.reasonModelDisplayName,
				reasonTreeNodeUid: event.reasonTreeNodeUid,
				eventCategoryUid: event.eventCategoryUid,
				reasonModelThingName: event.reasonModelName,
				comments: event.notes,
				isAutomation: event.isAutomation,// || event.dataShapeName == AVAILABILITY_EVENT, // BOOLEAN
				uid: event.eventUid // STRING
			});
		});

		result.Sort({
			"name": "eventTime",
			"ascending": false
		});
	}
} catch (err) {
	logger.error("Thing [{}] Service [{}] error at line [{}] : {}", me.name, err.fileName, err.lineNumber, err);
	throw me.Localize({
		token: "PTC.Base.ErrorMessage"
	});
}

function getImpact(event) {
	switch (event.dataShapeName) {
		case AVAILABILITY_EVENT:
			return event.duration;
		case LOSS_EVENT:
			return event.lossValue;
		case PRODUCTION_EVENT:
			return event.quantity;
	}
}

function getImpactDisplay(impact, event) {
	let impactDisplay;
	let showAsAutomation = event.isAutomation;

	switch (event.dataShapeName) {
		case AVAILABILITY_EVENT:
			impactDisplay = formatTime(impact * SEC_IN_MIN, false);
			showAsAutomation = true;
			break;
		case LOSS_EVENT:
			impactDisplay = formatTime(impact, false);
			break;
		case PRODUCTION_EVENT:
			impactDisplay = event.quantity + " " + (event.quantity === 1 ? PART : PARTS) + " (" + formatTime(event.duration * SEC_IN_MIN, false) + ")";
			break;
		default:
			impactDisplay = "-";
	}

	let iconDisplay = "<span style='font-size:14px;" + (showAsAutomation ? "color:#ADB5BD;" : "") + "'>" + impactDisplay + (showAsAutomation ? " *" : "") + "</span>";
	if (event.hasChildrenReasons) {
		iconDisplay = iconDisplay + "<span style='margin:5px'><img id='warning' width='18' height='18' src='/Thingworx/MediaEntities/PTC.ProductionDashboard.ErrorIcon_MD'/></span>";
	}
	return iconDisplay;
}

function stringifyIntegerOrFloat(value) {
	return String(+parseFloat(value).toFixed(1));
}
				    function formatTime(unformattedTime, showCentiseconds) {
	let formattedTime = "";
	if (showCentiseconds) {
		formattedTime = baseCommonHelper.FormatDurationWithCentiSeconds({
			duration: unformattedTime
		});
	} else {
		formattedTime = baseCommonHelper.FormatDuration({
			duration: unformattedTime
		});
	}

	return formattedTime;
}
function getEventEndTime(event) {
	let eventLogIds = DataShapes["PTC.ProductionDashboard.EventLogs"].CreateValues();
	let newEntry = {
		eventId: event.eventUid + "_" + event.dataShapeName,
	};
	eventLogIds.AddRow(newEntry);
	let endDate = me.GetEventsDataByIds({
		thingName: thingName,
		events: eventLogIds
	}).endDate;
	return endDate;
}
function getImpactDuration(event) {
	switch (event.dataShapeName) {
		case AVAILABILITY_EVENT:
			return event.duration * SEC_IN_MIN;
		case LOSS_EVENT:
			return event.lossValue;
		case PRODUCTION_EVENT:
			return event.quantity;
	}
}
function setHasChildrenReasons(event) {
	let childrenReasons = Things[reasonCodeManager].GetAllChildrenReasonTreeNodes({
		parentReasonTreeNodeUid: event.reasonTreeNodeUid
	});
	if (childrenReasons.length > 0) {
		event.hasChildrenReasons = true;
	}
	if (!event.reasonTreeNodeUid || event.reasonTreeNodeUid == 0) {
		event.reasonTreeNodeUid = "RC_UID_" + event.reasonCategoryUid;
		event.hasChildrenReasons = true;
	}
}

const AVAILABILITY_EVENT = "PTC.OperationKPI.AvailabilityEvent";
const MIN_EVENT_AGE = me.GetMinEventAgeInDays();
const CAN_EDIT_MANUAL_EVENTS = me.CanEditManualEvents();
const CAN_EDIT_AUTOMATED_EVENTS = me.CanEditAutomatedEvents();
const CAN_DELETE_MANUAL_EVENTS = me.CanDeleteManualEvents();

let result = DataShapes["PTC.ProductionDashboard.EventLogs"].CreateValues();
try {
	result = me.GetEventsByProductionBlock({
		productionBlockUid: productionBlockUid,
		searchLossReason: searchLossReason,
		searchReasonCategory: searchReasonCategory,
		thingName: thingName,
		searchWorkUnitCausingEvent: searchWorkUnitCausingEvent
	});

	result.rows.toArray().forEach(event => {
		event.canEditEvent = canEditEvent(event);
		event.canDeleteEvent = canDeleteEvent(event);
	});
} catch (err) {
	logger.error("Thing [{}] Service [{}] error at line [{}] : {}", me.name, err.fileName, err.lineNumber, err);
	throw me.Localize({
		token: "PTC.Base.ErrorMessage"
	});
}

function canEditEvent(event) {
	let canEdit = false;
	let currTime = new Date();
	let eventTime = new Date();
	let difference = 0;
	let isAutomatedEvent = false;
	let hourDiff = 0;
	let dayDiff = 0;
	if (event) {
		eventTime = new Date(event.eventTime);
		difference = dateDifference(currTime, eventTime);
		isAutomatedEvent = event.isAutomation;// || event.dataShapeName == AVAILABILITY_EVENT;
		hourDiff = difference / (1000 * 60 * 60);
		dayDiff = hourDiff / 24;
		if (isAutomatedEvent) {
			if (dayDiff <= MIN_EVENT_AGE && CAN_EDIT_AUTOMATED_EVENTS) {
				canEdit = true;
			} else if (hourDiff <= 24) {
				canEdit = true;
			}
		} else {
			if (dayDiff <= MIN_EVENT_AGE && CAN_EDIT_MANUAL_EVENTS) {
				canEdit = true;
			} else if (hourDiff <= 24) {
				canEdit = true;
			}
		}
	}
	return canEdit;
}

function canDeleteEvent(event) {
	let canDelete = false;
	let currTime = new Date();
	let eventTime = new Date();
	let difference = 0;
	let isAutomatedEvent = false;
	let hourDiff = 0;
	let dayDiff = 0;
	if (event) {
		eventTime = event.eventTime;
		difference = dateDifference(currTime, eventTime);
		isAutomatedEvent = event.isAutomation;// || event.dataShapeName == AVAILABILITY_EVENT;
		hourDiff = difference / (1000 * 60 * 60);
		dayDiff = hourDiff / 24;
		if (!isAutomatedEvent) {
			if (hourDiff <= 24) {
				canDelete = true;
			} else if (dayDiff <= MIN_EVENT_AGE && CAN_DELETE_MANUAL_EVENTS) {
				canDelete = true;
			}
		}
	}
	return canDelete;
}

