	throw me.Localize({token: "PTC.Base.ErrorMessage"});
}

try {
	var result = DataShapes["PTC.ProductionDashboard.EventLogs"].CreateValues();

	const SEC_TO_MIN = 60;
	const AVAILABILITY_EVENT = "PTC.OperationKPI.AvailabilityEvent";
	const LOSS_EVENT = "PTC.OperationKPI.LossEvent";
	const PRODUCTION_EVENT = "PTC.OperationKPI.ProductionEvent";
	const USER_LOCALE = me.GetCurrentUserLocale();
    const CAN_EDIT_MANUAL_EVENTS = me.CanEditManualEvents();
    const CAN_EDIT_AUTOMATED_EVENTS = me.CanEditAutomatedEvents();
    const CAN_DELETE_MANUAL_EVENTS = me.CanDeleteManualEvents();
    const MIN_EVENT_AGE = me.GetMinEventAgeInDays();

	let events = Things[thingName].GetPTCNegativeEvents({
		startDateTime: startDateTime,
		endDateTime: endDateTime,
		locale: USER_LOCALE,
		offset: offset,
		limit: limit,
		filterBy: filterBy,
		sortBy: sortBy
	});

	events.rows.toArray().forEach(event => {

		result.AddRow({
			modelUid: event.modelUid,
			eventTime: event.eventTime,
			startDate: event.eventTime,
			eventTypeUid: event.eventTypeUid,
			eventTypeName: event.eventTypeName,
			eventCategoryUid: event.eventCategoryUid,
			eventCategoryName: event.eventCategoryName,
			reasonTypeUid: event.reasonTypeUid,
			reasonName: event.reasonName,
			reasonCategoryUid: event.reasonCategoryUid,
			reasonTreeNodeUid: event.reasonTreeNodeUid,
			reasonCategoryName: event.reasonCategoryName,
            reasonCategoryNameDisplay: event.reasonCategoryNameDisplay,
			productionBlockStartTime: event.productionBlockStartTime,
			productionBlockUid: event.productionBlockUid,
			endDate: event.endTime,
			dataShapeName: event.dataShapeName,
			comments: event.notes,
			shiftName: event.shiftName,
			materialMasterId: event.materialMasterId,
			jobOrderId: event.jobOrderId,
			hasChildrenReasons: event.hasChildrenReasons,
			uid: event.eventUid,
			impact: getImpact(event),
			impactDuration: getImpactDuration(event),
			reasonModelThingName: event.reasonModelName,
			isAutomation: event.isAutomation, //|| event.dataShapeName == AVAILABILITY_EVENT,
			splittable: event.dataShapeName == AVAILABILITY_EVENT,
			reasonTypeName: event.reasonTypeName || undefined,
			eventId: event.eventUid + "_" + event.dataShapeName,
			eventCategoryNameDisplay: event.eventCategoryNameDisplay,
			updatedTime: event.updatedTime,
			reasonModelDisplayName: event.reasonModelDisplayName,
			canEditEvent: canEditEvent(event),
			canDeleteEvent: canDeleteEvent(event)
		});
	});
} catch (err) {
	logger.error("Thing [{}] Service [{}] error at line [{}] : {}", me.name, err.fileName, err.lineNumber, err);
	throw me.Localize({
		token: "PTC.Base.ErrorMessage"
	});
}

function getImpactDuration(event) {
	switch (event.dataShapeName) {
		case AVAILABILITY_EVENT:
			return event.duration;
		case LOSS_EVENT:
			return event.lossValue;
		case PRODUCTION_EVENT:
			return event.quantity;
	}
}

function getImpact(event) {
	switch (event.dataShapeName) {
		case AVAILABILITY_EVENT:
			return event.duration / SEC_TO_MIN;
		case LOSS_EVENT:
			return event.lossValue;
		case PRODUCTION_EVENT:
			return event.quantity;
	}
}


function canEditEvent(event) {
	let canEdit = false;
	let currTime = new Date();
	let eventTime = new Date();
	let difference = 0;
	let isAutomatedEvent = false;
	let hourDiff = 0;
	let dayDiff = 0;
	if (event) {
		eventTime = new Date(event.eventTime);
		difference = dateDifference(currTime, eventTime);
		isAutomatedEvent = event.isAutomation;// || event.dataShapeName == AVAILABILITY_EVENT;
		hourDiff = difference / (1000 * 60 * 60);
		dayDiff = hourDiff / 24;
		if (isAutomatedEvent) {
			if (dayDiff <= MIN_EVENT_AGE && CAN_EDIT_AUTOMATED_EVENTS) {
				canEdit = true;
			} else if (hourDiff <= 24) {
				canEdit = true;
			}
		} else {
			if (dayDiff <= MIN_EVENT_AGE && CAN_EDIT_MANUAL_EVENTS) {
				canEdit = true;
			} else if (hourDiff <= 24) {
				canEdit = true;
			}
		}
	}
	return canEdit;
}

function canDeleteEvent(event) {
	let canDelete = false;
	let currTime = new Date();
	let eventTime = new Date();
	let difference = 0;
	let isAutomatedEvent = false;
	let hourDiff = 0;
	let dayDiff = 0;
	if (event) {
		eventTime = event.eventTime;
		difference = dateDifference(currTime, eventTime);
		isAutomatedEvent = event.isAutomation;// || event.dataShapeName == AVAILABILITY_EVENT;
		hourDiff = difference / (1000 * 60 * 60);
		dayDiff = hourDiff / 24;
		if (!isAutomatedEvent) {
			if (hourDiff <= 24) {
				canDelete = true;
			} else if (dayDiff <= MIN_EVENT_AGE && CAN_DELETE_MANUAL_EVENTS) {
				canDelete = true;
			}
		}
	}
	return canDelete;
}

