const GENERIC_STR_LIST_DS = "GenericStringList";
const NONE_VALUE = "--";
const EMPTY_VALUE = "";

let adjustedDates = me.GetAdjustedStarAndEndDateTimes({
	startDate: startDate,
	endDate: endDate
});

try {
	var result = DataShapes["PTC.Scorecard.MetricSummary"].CreateValues();
	if (adjustedDates.startDateTime && adjustedDates.endDateTime && modelUid) {
		let metrics = me.GetAllMetrics();
		let scorecardSummaryInfos = getScorecardSummaryInfos(metrics, adjustedDates.startDateTime, adjustedDates.endDateTime);
		let targets = getTargets(metrics, adjustedDates.startDateTime, adjustedDates.endDateTime);
		let statusThresholds = getStatusThresholds(metrics, adjustedDates.startDateTime, adjustedDates.endDateTime);

		metrics.rows.toArray().forEach(metric => {
			let scorecardSummaryInfo = scorecardSummaryInfos.Find({
				metricName: metric.metricName
			});

			let target = targets.Find({
				metricUid: metric.metricUid
			});

			let statusThreshold = statusThresholds.Find({
				metricUid: metric.metricUid
			});

			result.AddRow({
				metricUid: metric.metricUid,
				metricName: metric.metricName,
				statusDisplay: getStatusCodeDisplay(metric, statusThreshold, scorecardSummaryInfo),
				actualDisplay: getActualDisplay(metric, scorecardSummaryInfo),
				targetDisplay: getTargetDisplay(metric, target),
				percentageChangeDisplay: getPercentageChangeDisplay(metric, scorecardSummaryInfo),
				metricNameDisplay: getMetricNameDisplay(metric)
			});
		});
	}
} catch (err) {
	logger.error("Thing [{}] Service [{}] error at line [{}] : {}", me.name, err.fileName, err.lineNumber, err);
	throw localize("PTC.Base.ErrorMessage");
}

function getActualDisplay(metric, scorecardSummaryInfo) {
	if (!scorecardSummaryInfo) {
		return NONE_VALUE;
	}
	return getFormattedValue(metric.unitOfMeasureAbbreviation, scorecardSummaryInfo.actual);
}

function getMetricNameDisplay(metric) {
	let metricName = metric.metric_name_localized ? metric.metric_name_localized : metric.metricName;
	let metricUom = metric.uom_abbreviation_localized;
	return metricName + " (" + metricUom + ")";
}

function getPercentageChangeDisplay(metric, scorecardSummaryInfo) {
	if (!scorecardSummaryInfo || isNaN(scorecardSummaryInfo.percentageChange)) {
		return NONE_VALUE;
	}

	let percentageChange = scorecardSummaryInfo.percentageChange;
	let arrowIcon = me.GetPercentageChangeIcon({
		percentageChange: percentageChange,
		correlation: metric.correlationName
	}).arrowIcon;

	return "<span style='font-size:14px'><img id='img' style='vertical-align:middle' src='" + arrowIcon + "'/> " + Math.abs(percentageChange.toFixed(1)) + " %</span>";
}

function getStatusCodeDisplay(metric, statusThreshold, scorecardSummaryInfo) {
	let statusIconAndLabel = me.GetStatusIconAndLabel({
		correlation: metric.correlationName,
		poorToOkStatusThreshold: statusThreshold ? statusThreshold.poorToOkStatusThreshold : undefined,
		okToGoodStatusThreshold: statusThreshold ? statusThreshold.okToGoodStatusThreshold : undefined,
		actual: scorecardSummaryInfo ? scorecardSummaryInfo.actual: undefined
	});
	if (!statusIconAndLabel || !statusIconAndLabel.statusIcon) {
		return NONE_VALUE;
	} else {
		return "<span style='font-size:14px'><img id='img' style='vertical-align:middle' src='" + statusIconAndLabel.statusIcon + "'/> " + statusIconAndLabel.localizedStatusLabel + "<span>";
	}
}

function getTargetDisplay(metric, target) {
	if (!target) {
		return NONE_VALUE;
	}
	return getFormattedValue(metric.unitOfMeasureAbbreviation, target.target);
}

function getScorecardSummaryInfos(metrics, startDateTime, endDateTime) {
	let metricNames = DataShapes[GENERIC_STR_LIST_DS].CreateValues();
	metrics.getRows().toArray().forEach(row => {
		metricNames.AddRow({
			item: row.metricName
		});
	});

	return Things[me.GetKPIAnalysisManager()].GetScorecardSummaryInfos({
		modelUid: modelUid,
		metricNames: metricNames,
		startDateTime: startDateTime,
		endDateTime: endDateTime,
		timeRatio: 3600
	});
}

function getStatusThresholds(metrics, startDateTime, endDateTime) {
	let metricUids = DataShapes[GENERIC_STR_LIST_DS].CreateValues();
	metrics.getRows().toArray().forEach(row => {
		metricUids.AddRow({
			item: row.metricUid
		});
	});

	return me.GetStatusThresholdForMetrics({
		metricUids: metricUids,
		modelUid: modelUid,
		startDateTime: startDateTime,
		endDateTime: endDateTime
	});
}

function getTargets(metrics, startDateTime, endDateTime) {
	let metricUids = DataShapes[GENERIC_STR_LIST_DS].CreateValues();
	metrics.getRows().toArray().forEach(row => {
		metricUids.AddRow({
			item: row.metricUid
		});
	});

	return me.GetTargetForMetrics({
		metricUids: metricUids,
		modelUid: modelUid,
		startDateTime: startDateTime,
		endDateTime: endDateTime
	});
}

function getFormattedValue(uom, value) {
	if (!isValidNumber(value)) {
		return EMPTY_VALUE;
	}

	return me.GetFormattedValue({
		uom: uom,
		value: value
	}).toString();
}

function localize(token) {
	return me.LocalizeToken({
		stringToken: token
	});
}

function isValidNumber(input) {
	return typeof input === 'number';
}
