function queryBuilder(equipment, reasonCategoriesTable, reasonCategoriesRows, status, owner) {
	const query = {
		filters: {
			type: 'AND',
			filters: []
		}
	};
	const filters = query.filters.filters;

	if (equipment) {
		filters.push({
			fieldName: 'modelName',
			type: 'EQ',
			value: equipment
		});
	}

	if (reasonCategoriesRows > 0) {
		let reasonCategories = [];
		for (var i = 0; i < reasonCategoriesRows; i++) {
			reasonCategories.push(reasonCategoriesTable.rows[i].displayName);
		}
		filters.push({
			fieldName: "reasonCategoryName",
			type: 'IN',
			values: reasonCategories
		});
	}

	if (status) {
		filters.push({
			fieldName: 'statusDefinitionName',
			type: 'EQ',
			value: status
		});
	}

	if (owner) {
		filters.push({
			fieldName: 'owner',
			type: 'EQ',
			value: owner
		});
	}
	return query;
}

