			.map(row => callTotalTrend(operationKpiManager, startDate, endDate, modelUid, row.uid, reasonModelUid, userOffset, includeEndDateEdge))
			.reduce((totalTable, currentTable) => union(totalTable, currentTable), initialValueTable);

		// make result infotable from aggregate sum()
		let resultTableJSON = {
			dataShape: {
				fieldDefinitions: {
					losseventvalue: {
						name: "losseventvalue",
						baseType: "NUMBER"
					},
					eventtime: {
						name: "eventtime",
						baseType: "STRING"
					}
				}
			},
			rows: unionResult.rows.toArray().reduce((totalRows, currentRow) => sum(totalRows, currentRow), [])
		};

