		let modelType = mfgModelManager.GetModelType({
			modelUid: modelUid
		});

		let reasonModelUid;
		let workCenter;

		switch (modelType.name) {
			case "Workcenter":
				workCenter = model;
				break;

			case "Workunit":
				workCenter = mfgModelManager.GetWorkcenterFromWorkunit({
					workunitUid: modelUid
				});
				reasonModelUid = modelUid;
				break;

			default:
				let pacemakerUid = me.GetEquipmentNetworkWorkUnitUids({
					modelUid: modelUid,
					pacemakersOnly: true
				});

				workCenter = mfgModelManager.GetWorkcenterFromWorkunit({
					workunitUid: pacemakerUid
				});
		}

		let trendData = operationKpiManager.GetTrend2Infos({
			startDateTime: startDate,
			endDateTime: endDate,
			modelUid: modelUid,
			reasonTreeNodeUid: reasonTreeNodeUid,
			reasonCategoryUid: reasonCategoryUid,
			reasonModelUid: reasonModelUid,
			adjustingTimeOffset: userOffset
		});

		trendData.rows.toArray().forEach(row => {
			result.AddRow({
				eventTime: dateAddMinutes(parseDate(row.eventtime, "yyyy-MM-dd HH:mm:ss.SSSSSSS ZZ"), -1 * userOffset),
				lossEventValue: row.losseventvalue
			});
		});
	}
} catch (err) {
	logger.error("Thing [{}] Service [{}] error at line [{}] : {}", me.name, err.fileName, err.lineNumber, err);
	throw me.LocalizeToken({
		token: "PTC.Base.ErrorMessage"
	});
}

function isValidData(modelUid, startDate, endDate, reasonTreeNodeUid) {
	if (!modelUid || isNaN(modelUid) || modelUid <= 0) {
		return false;
	}

	if (!startDate || !endDate || startDate > Date.now()) {
		return false;
	}

	if (!reasonTreeNodeUid || isNaN(reasonTreeNodeUid) || reasonTreeNodeUid <= 0) {
		return false;
	}

	return true;
}

