function convertToJSON(string){
    let resultJSON = {};
    let array = string.split(",");
    array.forEach(keyValuePair => {
        let innerArray = keyValuePair.split(":");
        if(innerArray.length > 1){
            key = innerArray[0].trim();
            value = innerArray[1].trim();
        }
        resultJSON[key] = value;
    });
	return resultJSON;
}

