try {
    var result = DataShapes["PTC.MaterialMaster.MaterialMaster"].CreateValues();
    const MODEL_THING = Things[thingName];
    validateModel(MODEL_THING);

	let isWorkUnit = Things[me.GetMfgModelManager()].IsModelType({
        uid: MODEL_THING.GetPTCModelUid(),
        type: "Workunit"
    });
    let pacemakerModel = {};
    if(isWorkUnit){
        pacemakerModel.thingName = thingName;
    } else {
        pacemakerModel = MODEL_THING.GetPTCPacemakerWorkUnitInWorkCenter({
            workcenterUid: MODEL_THING.GetPTCModelUid()
        });
    }


    if (pacemakerModel && (isWorkUnit || pacemakerModel.length > 0)) {
        const pacemakerThingName = Things[pacemakerModel.thingName];
        validateModel(pacemakerThingName);

        result = pacemakerThingName.GetPTCAnalysisWorkMasterMaterials();
    }
} catch (err) {
    logger.error("Thing [{}] Service [{}] error at line [{}] : {}", me.name, err.fileName, err.lineNumber, err);
    throw localize("PTC.Base.ErrorMessage");
}

function validateModel(model) {
    if (!model) {
        throw new Error("Model information is missing.");
    }
}

function localize(token) {
    return me.LocalizeToken({
        stringToken: token
    });
}

let serviceName = "GetProductionBlockUidsByConditions";
if (logger.debugEnabled) {
    logger.debug("{}: equipmentInfo={}", serviceName, equipmentInfo);
    logger.debug("{}: distributionQueryParameters={}", serviceName, distributionQueryParameters);
}

let tlaManager = null;
try {
    let tlaManagerThingName = me.GetTimeLossManagerName();
    tlaManager = Things[tlaManagerThingName];
} catch (err) {
    logger.error("Thing [{}] Service [{}] error at line [{}] : {}", me.name, err.fileName, err.lineNumber, err);
    throw me.LocalizeToken({stringToken: "PTC.Base.ErrorMessage"});
}

let dataThingName = tlaManager.GetDataThingName();
let dataThing = Things[dataThingName];
if (!dataThing) {
    throw "Thingworx Analytics Data Thing does NOT exist";
}

if (!dataThing.isConnected) {
    throw "Thingworx Analytics Data Thing is NOT connected";
}

let distributionQueryParams = JSON.parse(distributionQueryParameters);

distributionQueryParams.queryFields.rows.push({item: "ProductionBlockUID"});

let distributionResultTable = null;
for (let i=0; (distributionResultTable === null) && i < 3; i++) {
    try {
        distributionResultTable = dataThing.DistributionQuerySynchronous(distributionQueryParams);
    }
    catch (err) {
        logger.error(serviceName+": Error running distribution query '" + JSON.stringify(distributionQueryParams) + "': " + err);
        distributionResultTable = null;
    }
}

if (distributionResultTable === null) {
    throw "Thingworx Analytics Data Thing service DistributionQuerySynchronous()' failed!";
}

let productionBlockUids = [];
distributionResultTable.rows.toArray().forEach(row => {
    row.groupByValues.rows.toArray().forEach(value => {
        productionBlockUids.push(Math.abs(value.fieldValue));
    });
});

equipmentInfo.productionBlockUIDs = unique(productionBlockUids).join(",");

result = equipmentInfo;

