function unique(a) {
    var seen = {};
    return a.filter(function(item) {
        return seen.hasOwnProperty(item) ? false : (seen[item] = true);
    });
}

try {
    var result = DataShapes["ToolbarAction"].CreateValues();

    let nextChartDisabled = true;
    let showAverageWeekDisabled = true;
    let showAverageWeekState = !!isShowAverageWeek;
    let viewEventLogDisabled = isEnterpriseOrRegion || disableEventLog;
    let nextChartVisible = true;
    if (navigationHistory && navigationHistory.length > 1) {
        let currentRow = navigationHistory.rows[navigationHistory.length - 2];
        let nextRow = navigationHistory.rows[navigationHistory.length - 1];
        if (currentRow.linkTarget) {
            let isTrend = isTrendMashup(currentRow.linkTarget);
            showAverageWeekDisabled = isTrend;
            nextChartVisible = !isTrend;
        }
        nextChartDisabled = disableNextButton(nextRow.title, isWorkcenter);
    }

    result.AddRow({
        actionId: "ShowAverageWeek",
        actionType: "toggle",
        actionLabel: me.LocalizeToken({stringToken: "PTC.PerformanceAnalysis.ShowAverageWeek"}),
        actionTooltip: me.LocalizeToken({stringToken: "PTC.PerformanceAnalysis.NormalizedButtonTooltip"}),
        actionDisabled: showAverageWeekDisabled,
        actionVisible: true,
        toggleState: showAverageWeekState,
        toggleChipIcon: true,
        toggleLabelPosition: 'left',
    });

    let viewInsightsTooltip = "";
    //MMM change to allow work unit selection
    if ((viewInsightsTooltip.trim().length == 0) && !isWorkCenterOrArea && !isWorkcenter) {
        viewInsightsTooltip = me.LocalizeToken({stringToken: "PTC.PerformanceAnalysis.NotWorkCenter"});
    }
    // End MMM
    if ((viewInsightsTooltip.trim().length == 0) && equipmentThingName && equipmentThingName.trim().length > 0) {
        viewInsightsTooltip = getViewInsightsTooltip(equipmentThingName);
    }

    if ((viewInsightsTooltip.trim().length == 0) && selectedBar && (selectedBar.getRowCount() > 0)) {
        viewInsightsTooltip = isStandardLossReason(selectedBar);
    }

    // Disable ViewInsights on Pareto chart
    let paretoLevel = (navigationHistory && navigationHistory.length) ? navigationHistory.length - 2 : 0;
    let viewInsightsDisabled=!((viewInsightsTooltip.trim().length == 0) && paretoLevel == 1);

    if (viewInsightsTooltip.trim().length == 0) {
         viewInsightsTooltip = me.LocalizeToken({stringToken: "PTC.PerformanceAnalysis.With.Dataset"});
    }

    result.AddRow({
        actionId      : "ViewInsights",
        actionType    : "button",
        actionLabel   : me.LocalizeToken({stringToken: "PTC.PerformanceAnalysis.ViewInsights"}),
        actionDisabled: viewInsightsDisabled,
        actionVisible : true,
        buttonType    : "transparent",
        buttonIcon    : "cds:icon_insights",
        alignRight    : false,
        actionTooltip : viewInsightsTooltip
    });

    result.AddRow({
        actionId: "NextChart",
        actionType: "button",
        actionLabel: me.LocalizeToken({stringToken: "PTC.PerformanceAnalysis.NextChart"}),
        actionDisabled: nextChartDisabled,
        actionVisible: nextChartVisible,
        buttonType: "Primary",
        alignRight: true,
    });

    let viewEventLogTooltip = "";
    if (viewEventLogDisabled) {
        viewEventLogTooltip = me.LocalizeToken({stringToken: "PTC.PerformanceAnalysis.EventLogTooltip"});
    }    
    result.AddRow({
        actionId: "ViewEventLog",
        actionType: "button",
        actionLabel: me.LocalizeToken({stringToken: "PTC.PerformanceAnalysis.ViewEventLog"}),
        actionDisabled: viewEventLogDisabled,
        actionVisible: true,
        buttonType: "transparent",
        buttonIcon: "cds:icon_list",
        actionTooltip: viewEventLogTooltip
    });

} catch (err) {
    logger.error("Thing [{}] Service [{}] error at line [{}] : {}", me.name, err.fileName, err.lineNumber, err);
    throw me.LocalizeToken({stringToken: "PTC.Base.ErrorMessage"});
}

