function isTrendMashup(currentMashupName) {
    let trendChartName = me.GetTrendMashupConfiguration();

    return trendChartName == currentMashupName;
}


function disableNextButton(nextLabel, isWorkcenter) {
    const NO_DATA_STRING = me.LocalizeToken({stringToken: "PTC.PerformanceAnalysis.NoData"});

    return nextLabel === NO_DATA_STRING || isWorkcenter === false ? true : false;
}

function getViewInsightsTooltip(equipmentThingName) {
    let key = "PTC.PerformanceAnalysis.AnalyticsServerNotConfigured";

    let areaThingName = equipmentThingName;
    // Determine if this is a workarea, in which case, get the parent area since that contains the dataset information.
    let thing = Things[equipmentThingName];
    let modelType = thing.GetPTCModelType();

    //MMM - Adding logic to handle workunits
    if (modelType.name.trim().toUpperCase() === "WORKUNIT") {        
        workCenterThingName = thing.GetPTCParent().thingName;
        thing = Things[workCenterThingName];
        modelType = thing.GetPTCModelType();
    }
    // End MMM

    if (modelType.name.trim().toUpperCase() === "WORKCENTER") {        
        areaThingName = thing.GetPTCParent().thingName;
    }

    let tlaManagerThingName = me.GetTimeLossManagerName();
    let tlaManager = Things[tlaManagerThingName];
    try {
        let dataThingName = tlaManager.GetDataThingName();
        if (dataThingName && (dataThingName.trim().length > 0)) {
            let dataThing = Things[dataThingName];
            if (dataThing && dataThing.isConnected) {
                if (isUploadInProgress(areaThingName)) {
                    key = "PTC.PerformanceAnalysis.Dataset.Creation.In.Progress";
                } else {
                    key = "PTC.PerformanceAnalysis.No.Dataset";
                    let datasetId = getDatasetId(areaThingName);
                    if (datasetId && datasetId.trim().length > 0) {
                        let status = tlaManager.GetDatasetStatus({datasetId: datasetId});
                        if (status.trim().toUpperCase() === "COMPLETED") {
                            key="";
                        } else if (status.trim().toUpperCase() === "FAILED") {
                            key = "PTC.PerformanceAnalysis.Dataset.Creation.Failed";
                        } else {
                            key = "PTC.PerformanceAnalysis.Dataset.Creation.In.Progress";
                        }
                    }
                }
            }
        }
    } catch (e) {}
    return me.LocalizeToken({stringToken: key});
}

function  isUploadInProgress(areaThingName) {
    let equipmentThing = Things[areaThingName];
    return equipmentThing.PTCAnalyticProcessingLock;
}

function  getDatasetId(areaThingName) {
    let equipmentThing = Things[areaThingName];
    return equipmentThing.PTCAnalyticDatasetID;
}

function isStandardLossReason(selectedBar) {
    if (!selectedBar || selectedBar.getRowCount() === 0) {
        return "";
    }
    let tlaManager=null;
    try {
        let tlaManagerThingName = me.GetTimeLossManagerName();
        tlaManager = Things[tlaManagerThingName];
    } catch (err) {
        logger.error("Thing [{}] Service [{}] error at line [{}] : {}", me.name, err.fileName, err.lineNumber, err);
        throw  me.LocalizeToken({stringToken: "PTC.Base.ErrorMessage"});
    }

    let reasonName = selectedBar.getRow(0).reasonName;
    let reasonUid = selectedBar.getRow(0).eventCategoryUid;
    let genericReason = getGenericReasonCategory(reasonName, reasonUid);
    let featureName = tlaManager.TranslateLossCategoryToFeatureName({lossCategory: genericReason});
    return (featureName.trim() !== "TimeLossCurrentPB") ? "" : me.LocalizeToken({stringToken: "PTC.PerformanceAnalysis.Not.Standard.Loss.Reason"});
}

function getGenericReasonCategory(reasonName, reasonCategoryUid) {
    try {
        let reasonCodeManagerThingName = me.GetConfiguredManagerForIdentifier({identifier: "PTC.ReasonCode.Manager"});
        let reasonCodeManager = Things[reasonCodeManagerThingName];
        let infoTable = reasonCodeManager.GetReasonCategory({uid: reasonCategoryUid});
        return (infoTable && infoTable.getRowCount() > 0) ? infoTable.getRow(0).name : reasonName;
    } catch (err) {
        logger.error("Thing [{}] Service [{}] error at line [{}] : {}", me.name, err.fileName, err.lineNumber, err);
    }
    return reasonName;
}

try {
    let isWorkUnit = Things[me.GetMfgModelManager()].IsModelType({
        uid: modelUid,
        type: "Workunit"
    });

    let isWorkCenter = Things[me.GetMfgModelManager()].IsModelType({
        uid: modelUid,
        type: "Workcenter"
    });

    result = (isWorkUnit || isWorkCenter);
} catch (err) {
    logger.error("Thing [{}] Service [{}] error at line [{}] : {}", me.name, err.fileName, err.lineNumber, err);
    throw me.LocalizeToken({stringToken: "PTC.Base.ErrorMessage"});
}

